var skadi = skadi || {};

skadi.X3Drag = class {

    constructor() {
        this.callbacks = {};
        this.elt = null;
        this.drag_overlay = document.getElementById("drag_overlay");
        this.start_time = null;
        this.start_pos = null;
    }

    set_element(elt) {
        this.elt = elt;
        let that = this;
        this.elt.onmousedown = function (evt) {
            that.start(evt);
        }
    }

    on(event, callback) {
        this.callbacks[event] = callback;
        return this;
    }

    start(evt) {
        let that = this;
        this.start_time = Date.now();
        evt.stopPropagation();

        document.onmousemove = function (evt) {
            that.initial_move(evt);
        }

        document.onmouseup = function (evt) {
            that.initial_mouseup(evt);
        }

        let pos = skadi.x3.get_event_xy(evt);
        this.start_pos = pos;
    }

    initial_move(evt) {
        document.onmousemove = null;
        document.onmouseup = null;
        this.enable_drag_overlay();
    }

    initial_mouseup(evt) {
        document.onmousemove = null;
        document.onmouseup = null;
    }

    enable_drag_overlay() {
        let that = this;
        this.drag_overlay.style = "pointer-events:all;";
        this.drag_overlay.setAttribute("width", "100%");
        this.drag_overlay.setAttribute("height", "100%");
        this.drag_overlay.setAttribute("class", "drag_overlay_open");

        this.drag_overlay.onmousemove = function (evt) {
            that.move(evt);
        }
        this.drag_overlay.onmouseup = function (evt) {
            that.end(evt);
        }
        this.drag_overlay_enabled = true;
    }

    start_cb() {
        if ("start" in this.callbacks) {
            this.callbacks["start"](this.start_pos.x, this.start_pos.y);
        }
        if ("start_abs" in this.callbacks) {
            this.callbacks["start_abs"](this.start_pos.ax, this.start_pos.ay);
        }
    }

    move(evt) {
        let elapsed_ms = Date.now() - this.start_time;
        if (elapsed_ms < 200) {
            return;
        }
        if (this.start_pos) {
            this.start_cb();
            this.start_pos = null;
        }
        evt.preventDefault();
        evt.stopPropagation();
        let pos = skadi.x3.get_event_xy(evt);
        if ("drag" in this.callbacks) {
            this.callbacks["drag"](pos.x, pos.y);
        }
        if ("drag_abs" in this.callbacks) {
            this.callbacks["drag_abs"](pos.ax, pos.ay);
        }
    }

    end(evt) {
        let elapsed_ms = Date.now() - this.start_time;
        if (elapsed_ms < 200) {
            // ignore drag end, this event should be followed by a click
        } else {
            if (this.start_pos) {
                this.start_cb();
                this.start_pos = null;
            }
            let pos = skadi.x3.get_event_xy(evt);
            evt.preventDefault();
            evt.stopPropagation();
            if ("end" in this.callbacks) {
                this.callbacks["end"](pos.x, pos.y);
            }
            if ("end_abs" in this.callbacks) {
                this.callbacks["end_abs"](pos.ax, pos.ay);
            }
        }
        this.drag_overlay.style = "pointer-events:none;";
        this.drag_overlay.setAttribute("width", 0);
        this.drag_overlay.setAttribute("height", 0);
        this.drag_overlay.setAttribute("class", "drag_overlay");
        this.drag_overlay.onmousemove = null;
        this.drag_overlay.onmouseup = null;
    }
}

skadi.X3Selection = class {

    constructor(elts) {
        this.svg_xmlns = "http://www.w3.org/2000/svg";
        this.xlink_xmlns = "http://www.w3.org/1999/xlink";
        this.html_xmlns = "http://www.w3.org/1999/xhtml";
        this.elts = [];

        for (let idx = 0; idx < elts.length; idx++) {
            this.elts.push(elts[idx]);
        }
    }

    add_node(node) {
        this.elts.push(node);
    }

    remove() {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].remove();
        }
        this.elts = [];
        return this;
    }

    is_svg(tag,parent_elt) {
        if (tag === "svg") {
            return true;
        }
        return parent_elt.namespaceURI === this.svg_xmlns;
    }

    append(tag, is_svg) {
        let newSelection = new skadi.X3Selection([]);
        for (let idx = 0; idx < this.elts.length; idx++) {
            let use_svg = is_svg;
            if (use_svg === undefined) {
                use_svg = this.is_svg(tag,this.elts[idx]);
            }
            let n;
            if (use_svg) {
                n = document.createElementNS(this.svg_xmlns, tag);
            } else {
                if (tag == "href") {
                    n = document.createElementNS(this.xlink_xmlns, tag);
                } else {
                    n = document.createElementNS(this.html_xmlns, tag);
                }
            }
            this.elts[idx].appendChild(n);
            newSelection.add_node(n);
        }
        return newSelection;
    }

    insert(tag, is_svg) {
        let newSelection = new skadi.X3Selection([]);
        for (let idx = 0; idx < this.elts.length; idx++) {
            let use_svg = is_svg;
            if (use_svg === undefined) {
                use_svg = this.is_svg(tag,this.elts[idx]);
            }
            let n;
            if (use_svg) {
                n = document.createElementNS(this.svg_xmlns, tag);
            } else {
                if (tag == "href") {
                    n = document.createElementNS(this.xlink_xmlns, tag);
                } else {
                    n = document.createElementNS(this.html_xmlns, tag);
                }
            }
            this.elts[idx].insertBefore(n, this.elts[idx].firstChild);
            newSelection.add_node(n);
        }
        return newSelection;
    }

    attr(name, value) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].setAttribute(name, value);
        }
        return this;
    }

    attr_ns(namespace, name, value) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].setAttributeNS(namespace, name, value);
        }
        return this;
    }

    text(txt) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            // this.elts[idx].innerHTML = "";
            let tn = document.createTextNode(txt);
            this.elts[idx].appendChild(tn);
        }
        return this;
    }

    add_class(cls) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].classList.add(cls);
        }
        return this;
    }

    remove_class(cls) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].classList.remove(cls);
        }
        return this;
    }

    html(inner_html) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].innerHTML = inner_html;
        }
        return this;
    }

    node() {
        if (this.elts) {
            return this.elts[0];
        } else {
            return null;
        }
    }

    style(name, value) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].style[name] = value;
        }
        return this;
    }

    call(drag) {
        if (this.elts.length === 1) {
            drag.set_element(this.elts[0]);
        } else {
            console.log("Unable to set up drag handler, not exactly 1 element in selection");
        }
    }

    on(event, callback) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].addEventListener(event, callback);
        }
        return this;
    }

    ignore_mouse_events() {
        ["mousemove"].forEach(event_type =>
            this.on(event_type, evt => {
                evt.stopPropagation();
                evt.preventDefault();
            }));
    }
}

skadi.X3 = class {

    constructor() {
    }

    select(selector) {
        let elts = document.querySelectorAll(selector);
        return new skadi.X3Selection(elts);
    }

    get_node_pos(elt) {
        let body = this.select('body').node();

        let x = 0;
        let y = 0;

        while (elt != null && elt != body) {
            x += (elt.offsetLeft || elt.clientLeft);
            y += (elt.offsetTop || elt.clientTop);
            elt = (elt.offsetParent || elt.parentNode);
        }

        return {"x": x, "y": y};
    }

    get_event_xy(e) {
        let pos = {};
        pos.x = e.clientX;
        pos.y = e.clientY;
        pos.ax = pos.x + window.scrollX;
        pos.ay = pos.y + window.scrollY;
        return pos;
    }

}

skadi.x3 = new skadi.X3();
skadi.x3.drag = function () {
    return new skadi.X3Drag();
}

skadi.$ = (id) => {
    return document.getElementById(id);
}

skadi.$$ = (selector) => {
    return skadi.x3.select(selector);
}

skadi.$$$ = (id) => {
    return new skadi.X3Selection([skadi.$(id)]);
}




/* src/js/common/alert.js */

var skadi = skadi || {};

skadi.Alert = class {

    constructor(message, state) {
        let w = skadi.$("core_alert");
        if (w) {
            w.remove();
        }
        let body = new skadi.X3Selection([document.body]);
        let parent = body.insert("div", false);
        parent.node().innerHTML = '<div id="core_alert" is="exo-modal" display="true" bg-color="light-gray" border-color="dark-purple" style="width:50%; height:50%;"></div>';

        w = skadi.$("core_alert");
        w.innerHTML="";
        let s = new skadi.X3Selection([w]);
        let d = s.append("div").attr("class","alert_dialogue");

        let img = null;
        switch (state) {
            case skadi.StatusStates.info:
                img = d.append("img").attr("src",skadi.icon_status_info);
                break;
            case skadi.StatusStates.warning:
                img = d.append("img").attr("src",skadi.icon_status_warning);
                break;
            case skadi.StatusStates.error:
                img = d.append("img").attr("src",skadi.icon_status_error);
                break;
        }
        d.append("h2").text(message);
        d.append("input").attr("value","OK").attr("type","button").on("click",(evt) => {
            parent.node().remove();
        });
    }
}


