var skadi = skadi || {};

skadi.IndexDB = class {

    constructor(name) {
        this.name = "topology-"+name;
    }

    async init() {
        this.db = await this.open();
    }

    async open() {
        return await new Promise((resolve,reject) => {
            const request = indexedDB.open(this.name, 1);
            request.onsuccess = (evt) => {
                resolve(evt.target.result);
            }
            request.onerror = (evt) => {
                console.error(evt.target.errorCode);
                resolve(null);
            }
            request.onupgradeneeded = (evt) => {
                // Save the IDBDatabase interface
                let db = evt.target.result;
                db.createObjectStore("data", {});
            }
        });
    }


    async get(key) {
        return await new Promise((resolve,reject) => {
            const transaction = this.db.transaction(["data"], "readonly");
            const request = transaction.objectStore("data").get(key);
            request.onsuccess = (evt) => {
                resolve(evt.target.result);
            }
            request.onerror = (evt) => {
                resolve(undefined);
            }
        });
    }

    async put(key, value) {
        return await new Promise((resolve,reject) => {
            const transaction = this.db.transaction(["data"], "readwrite");
            const request = transaction.objectStore("data").put(value,key);
            request.onsuccess = (evt) => {
                resolve(true);
            }
            request.onerror = (evt) => {
                resolve(true);
            }
        });
    }

    async delete(key) {
        return await new Promise((resolve,reject) => {
            const transaction = this.db.transaction(["data"], "readwrite");
            const request = transaction.objectStore("data").delete(key);
            request.onsuccess = (evt) => {
                resolve(evt.target.result);
            }
            request.onerror = (evt) => {
                resolve(undefined);
            }
        });
    }

    async getAllKeys() {
        return await new Promise((resolve,reject) => {
            const transaction = this.db.transaction(["data"], "readonly");
            const request = transaction.objectStore("data").getAllKeys();
            request.onsuccess = (evt) => {
                resolve(evt.target.result);
            }
            request.onerror = (evt) => {
                resolve(undefined);
            }
        });
    }
}

skadi.IndexDB.create = async function(name) {
    let db = new skadi.IndexDB(name);
    await db.init();
    return db;
}

skadi.IndexDB.remove = async function(name) {
    return await new Promise((resolve,reject) => {
        const request = indexedDB.deleteDatabase(name);
        request.onsuccess = (evt) => {
            resolve(true);
        }
        request.onerror = (evt) => {
            console.error(evt.target.errorCode);
            resolve(false);
        }
    });
}

/* src/js/utils/client_storage.js */

skadi = skadi || {};

skadi.ClientStorage = class {

    constructor(db_name) {
        this.db_name = db_name;
        this.db = null;
    }

    static check_valid_key(key) {
        if (!key.match(/^[0-9a-zA-Z_]+$/)) {
            throw new Error("data key can only contain alphanumeric characters and underscores");
        }
    }

    static check_valid_value(data) {
        if (data instanceof ArrayBuffer) {
            return;
        } else if (data === null) {
            return;
        }
        throw new Error("data value can only be null or ArrayBuffer")
    }

    async open() {
        this.db = await skadi.IndexDB.create(this.db_name);
    }

    close() {
        this.db = null;
    }

    async get_item(key) {
        if (!this.db) {
            await this.open();
        }
        let result = await this.db.get(key);
        if (result === undefined) {
            result = null;
        }
        return result;
    }

    async set_item(key, value) {
        if (!this.db) {
            await this.open();
        }
        await this.db.put(key, value);
    }

    async remove_item(key) {
        if (!this.db) {
            await this.open();
        }
        await this.db.delete(key);
    }

    async remove() {
        this.close();
        await skadi.IndexDB.remove(this.db_name);
    }

    async get_keys() {
        if (!this.db) {
            await this.open();
        }
        return await this.db.getAllKeys();
    }

    async clear() {
        // clear the database by removing and re-opening
        await this.remove();
        await this.open();
    }
}

/* src/js/utils/resource_loader.js */

var skadi = skadi || {};

skadi.ResourceLoader = class {

    constructor(js_parent, css_parent) {
        this.loading_resources = {}; // resource_name -> true
        this.loaded_resources = {}; // resource name -> true|false
        this.js_parent = js_parent || document.head;
        this.css_parent = css_parent || document.head;
    }

    async css_load(resource) {
        let that = this;
        await fetch(resource).then(r => r.text(), e => that.notify(resource,false)).then(txt => {
            let ele = document.createElement("style");
            ele.appendChild(document.createTextNode(txt));
            this.css_parent.appendChild(ele);
            that.notify(resource,true);
        });
    }

    async js_load(resource) {
        let script = document.createElement("script");
        script.onload = (evt) => this.notify(resource, true);
        script.setAttribute("src",resource);
        document.head.appendChild(script);
        while(true) {
            await new Promise(r => setTimeout(r, 100));
            if (resource in this.loaded_resources) {
                break;
            }
        }
    }

    async load(resource) {
        // start new load
        this.loading_resources[resource] = true;
        if (resource.endsWith(".css")) {
            await this.css_load(resource);
        } else  {
            await this.js_load(resource);
        }
    }

    notify(resource,success) {
        delete this.loading_resources[resource];
        this.loaded_resources[resource] = success;
    }

    async load_resources(resource_list) {
        let load_tasks = [];
        for(let idx=0; idx<resource_list.length; idx++) {
            let resource = resource_list[idx];
            if (resource in this.loading_resources || resource in this.loaded_resources) {
                /* resource already loaded or failed to load */
            } else {
                await this.load(resource);
            }
        }
        let results = {};
        for(let idx=0; idx<resource_list.length; idx++) {
            let resource = resource_list[idx];
            results[resource] = this.loaded_resources[resource];
        }
        return results;
    }
}



