var skadi = skadi || {};

skadi.Network = class {

    constructor() {
        this.nodes = {};
        this.links = {};
        this.configurations = {};
    }

    clear() {
        this.links = {};
        this.nodes = {};
    }

    get_downstream_nodes(from_node_id) {
        let nodes = [];
        let start_nodes = [from_node_id];
        do {
            let next_gen = [];
            for(let id in this.links) {
                let link = this.links[id];
                let from_node_id = link.get_from_node().get_id();
                let to_node_id = link.get_to_node().get_id();
                if (start_nodes.includes(from_node_id)) {
                    if (!nodes.includes(to_node_id)) {
                        nodes.push(to_node_id);
                        next_gen.push(to_node_id);
                    }
                }
            }
            start_nodes = next_gen;
        } while(start_nodes.length > 0);
        return nodes;
    }

    get_upstream_nodes(from_node_id) {
        let nodes = [];
        let start_nodes = [from_node_id];
        do {
            let next_gen = [];
            for(let id in this.links) {
                let link = this.links[id];
                let from_node_id = link.get_from_node().get_id();
                let to_node_id = link.get_to_node().get_id();
                if (start_nodes.includes(to_node_id)) {
                    if (!nodes.includes(from_node_id)) {
                        nodes.push(from_node_id);
                        next_gen.push(from_node_id);
                    }
                }
            }
            start_nodes = next_gen;
        } while(start_nodes.length > 0);
        return nodes;
    }

    get_connected_output_nodes(node_id, output_port_name) {
        let connected_nodes = [];
        for (let link_id in this.links) {
            let link = this.links[link_id];
            let from_node_id = link.get_from_node().get_id();
            if (from_node_id === node_id && link.get_from_port_name() === output_port_name) {
                connected_nodes.push([link.get_to_node(),link.get_to_port_name()]);
            }
        }
        return connected_nodes;
    }

    get_connected_input_nodes(node_id, input_port_name) {
        let connected_nodes = [];
        for (let link_id in this.links) {
            let link = this.links[link_id];
            let to_node_id = link.get_to_node().get_id();
            if (to_node_id === node_id && link.get_to_port_name() === input_port_name) {
                connected_nodes.push([link.get_from_node(),link.get_from_port_name()]);
            }
        }
        return connected_nodes;
    }

    /* configurations */

    add_configuration(configuration) {
        this.configurations[configuration.get_id()] = configuration;
    }

    get_configuration(package_id) {
        return this.configurations[package_id];
    }

    has_configuration(package_id) {
        return (package_id in this.configurations);
    }

    get_configuration_list() {
        let list = [];
        for(let id in this.configurations) {
            list.push(id);
        }
        return list;
    }

    /* nodes */

    add_node(node) {
        this.nodes[node.get_id()] = node;
    }

    get_node(id) {
        return this.nodes[id];
    }

    has_node(id) {
        return (id in this.nodes);
    }

    get_node_list() {
        let list = [];
        for(let id in this.nodes) {
            list.push(id);
        }
        return list;
    }

    remove_node(id) {
        // links to/from this node should have already been removed
        delete this.nodes[id];
    }

    get_connection_counts(node_id) {
        let counts = { "inputs":{}, "outputs":{}};
        for(let link_id in this.links) {
            let link = this.links[link_id];
            let from_node_id = link.get_from_node().get_id();
            if (from_node_id === node_id) {
                let from_port = link.get_from_port_name();
                if (!(from_port in counts.outputs)) {
                    counts.outputs[from_port] = 0;
                }
                counts.outputs[from_port] = counts.outputs[from_port]+1;
            }
            let to_node_id = link.get_to_node().get_id();
            if (to_node_id === node_id) {
                let to_port = link.get_to_port_name();
                if (!(to_port in counts.inputs)) {
                    counts.inputs[to_port] = 0;
                }
                counts.inputs[to_port] = counts.inputs[to_port]+1;
            }
        }
        return counts;
    }

    /* links */

    get_link(id) {
        return this.links[id];
    }

    add_link(link) {
        this.links[link.get_id()] = link;
    }

    has_link(id) {
        return (id in this.links);
    }

    remove_link(id) {
        delete this.links[id];
    }

    get_link_list() {
        let linklist = [];
        for(let link_id in this.links) {
            linklist.push(link_id);
        }
        return linklist;
    }

    extract_address(s) {
        let obj = {};
        let split = s.split(":");
        obj["node"] = split[0];
        obj["port"] = split[1];
        return obj;
    }

    contains_id(id) {
        return (id in this.nodes || id in this.links);
    }

    remove(id) {
        if (id in this.nodes) {
            this.remove_node(id);
        }
        if (id in this.links) {
            this.remove_link(id);
        }
    }

    /* packages */

    get_package_ids_used_by_nodes() {
        let package_ids = [];
        for(let node_id in this.nodes) {
            let package_id = this.nodes[node_id].get_type().get_package_id();
            if (!package_ids.includes(package_id)) {
                package_ids.push(package_id);
            }
        }
        return package_ids;
    }

  }


