var skadi = skadi || {};

skadi.Application = class  {

    constructor(core) {
        this.core = core;

        this.configuration_page_frames = {};
        this.node_page_frames = {};
        this.node_statuses = {};
        this.configuration_statuses = {};

        this.node_status_handler = this.core.add_node_status_event_handler((node_id,state,message) => {
            this.node_statuses[node_id] = { "state":state, "message":message };
            this.update_node_status_area(node_id, state, message);
        });

        this.configuration_status_handler = this.core.add_configuration_status_event_handler((package_id,state,message) => {
            this.configuration_statuses[package_id] = { "state":state, "message":message };
            this.update_configuration_status_area(package_id, state, message);
        });

        this.core.add_node_execution_event_handler((node_id, state) => {
            this.update_execution_state(node_id, state);
        });
    }

    get_id() {
        return this.core.get_id();
    }

    get_node_ids() {
        return this.core.get_network().get_node_list();
    }

    get_configuration_ids() {
        return this.core.get_network().get_configuration_list();
    }

    get_node(node_id) {
        return this.core.get_node(node_id);
    }

    get_configuration(package_id) {
        return this.core.get_configuration(package_id);
    }

    get_node_page_ids(node_id) {
        let node = this.core.get_network().get_node(node_id);
        if (node) {
            return node.get_page_ids();
        } else {
            return null;
        }
    }

    get_configuration_page_ids(package_id) {
        let configuration = this.core.get_network().get_configuration(package_id);
        if (configuration) {
            return configuration.get_page_ids();
        } else {
            return null;
        }
    }

    create_configuration_page(div,configuration,page_id,show_title,show_status,close_listener) {
        let page = configuration.get_page(page_id);
        let url = configuration.get_package_type().get_resource_url(configuration.get_url(page_id));
        let pf = new skadi.PageFrame(configuration,page_id,page.title,url,div,show_title,show_status,close_listener);
        if (!(configuration.get_id() in this.configuration_page_frames)) {
            this.configuration_page_frames[configuration.get_id()] = {};
        }
        this.configuration_page_frames[configuration.get_id()][page_id] = pf;
    }

    create_node_page(div,node,page_id,show_title,show_status,close_listener) {
        let page = node.get_type().get_page(page_id);
        let name = node.get_metadata()["name"];
        let page_name = name ? name + ": " + page.title : page.title;
        let url = node.get_type().get_package_type().get_resource_url(node.get_url(page_id));
        let pf = new skadi.PageFrame(node,page_id,page_name,url,div,show_title,show_status,close_listener);
        if (!(node.get_id() in this.node_page_frames)) {
            this.node_page_frames[node.get_id()] = {};
        }
        this.node_page_frames[node.get_id()][page_id] = pf;
    }

    update_node_status(id, state, status_message) {
        this.update_node_status_area(id, state, status_message);
    }

    update_configuration_status(id, state, status_message) {
        this.update_configuration_status_area(id, state, status_message);
    }

    update_node_status_area(node_id, state, status_message) {
        if (node_id in this.node_page_frames) {
            for (let page_id in this.node_page_frames[node_id]) {
                this.node_page_frames[node_id][page_id].update_status(state, status_message);
            }
        }
    }

    update_configuration_status_area(package_id, state, status_message) {
        if (package_id in this.configuration_page_frames) {
            for (let page_id in this.configuration_page_frames[package_id]) {
                this.configuration_page_frames[package_id][page_id].update_status(state, status_message);
            }
        }
    }

    update_execution_state(node_id, state) {
        if (node_id in this.node_page_frames) {
            let page_frames = this.node_page_frames[node_id];
            for(let page_id in page_frames) {
                this.node_page_frames[node_id][page_id].update_execution_state(state);
            }
        }
    }

    async deserialise(from_obj, node_renamings) {
        await this.core.deserialise(from_obj, node_renamings);
    }

    close() {
        this.core.remove_node_status_event_handler(this.node_status_handler);
        this.core.remove_configuration_status_event_handler(this.configuration_status_handler);
    }

}

