var skadi = skadi || {};

skadi.PageFrame = class {

    constructor(target, page_id, title, url, parent_div, show_title, show_status, close_listener) {
        this.target = target;
        this.page_id = page_id;
        this.show_title = show_title;
        this.show_status = show_status;
        this.close_listener = close_listener;

        this.div = parent_div.append("div").attr("class", "page_container").style("width", "100%")
            .style("height", "100%");

        this.titlebar = this.show_title ? this.div.append("div").style("width", "100%").attr("class", "titlebar") : null;
        if (this.titlebar && title) {
            this.titlebar.append("span").attr("class","titletext").text(title);
        }
        if (this.close_listener) {
            this.close_btn = this.titlebar.append("img").attr("src", skadi.icon_close_purple).attr("class", "close_btn")
                .on("click",(evt) => {
                    this.close();
                    this.div.remove();
                    this.close_listener();
                });
        }

        let iframe = this.div.append("iframe").attr("class", "page_iframe")
            .attr("src", url).attr("target", "_new").node();
        
        iframe.addEventListener("load", (ev) => {
            let wrapper = target.get_wrapper();
            if (wrapper) {
                wrapper.open_client(iframe.contentWindow, page_id);
            }
        });
        if (this.show_status) {
            this.footerbar = this.div.append("div").attr("class", "footerbar");
            this.status_icon = this.footerbar.append("span");
            this.status_area = this.footerbar.append("span");
        } else {
            this.footerbar = null;
            this.status_icon = null;
            this.status_area = null;
        }
    }

    update_status(state, status_message) {
        if (this.show_status) {
            this.status_icon.node().innerHTML = "";
            this.status_area.node().innerHTML = "";
            this.status_area.text(status_message);
            let icon = skadi.create_icon_for_status_state(state);
            if (icon) {
                this.status_icon.node().appendChild(icon);
            }
        }
    }

    update_execution_state(execution_state) {
        console.log(execution_state);
        if (this.titlebar) {
            this.titlebar.attr("class","titlebar "+execution_state);
        }
    }

    close() {
        let wrapper = this.target.get_wrapper();
        if (wrapper) {
            this.target.get_wrapper().close_client(this.page_id);
        }
    }
}

