/* src/js/common/geometry.js */

var skadi = skadi || {};

skadi.Geometry = class {

    constructor() {
    }

    get_corner_points(x,y,corners,r,smoothing) {
        smoothing = 0.1;
        let points = this.compute_polygon_points(x,y,corners,r);
        if (!smoothing) {
            return this.compute_standard_path(points);
        } else {
            return this.compute_smoothed_path(points,smoothing);
        }
    }

    compute_polygon_points(x,y,corners,r) {
        let points = [];

        let step = (Math.PI*2)/corners;

        let theta = 0;
        let dist = r;
        if (corners == 3) {
            dist *= 1.4;
        }
        if (corners == 4) {
            dist *= 1.2;
            theta -= step/2;
        }

        for(let i=0; i<corners; i++) {
            let px = x+dist*Math.sin(theta);
            let py = y-dist*Math.cos(theta);
            theta += step;
            points.push([px,py]);
        }
        return points;
    }

    compute_smoothed_path(points,smoothing) {
        let s = '';
        let segments = [];
        for(let idx=0; idx<points.length;idx++) {
            let c = points[idx];
            let p = null;
            if (idx == 0) {
                s += "M ";
                s += c[0] + "," + c[1];
                p = points[points.length-1];
            } else {
                p = points[idx-1];
            }

            let f0 = smoothing;
            let f1 = 1.0 - smoothing;

            let p1x = c[0]*f0+p[0]*f1;
            let p1y = c[1]*f0+p[1]*f1;

            let p2x = c[0]*f1+p[0]*f0;
            let p2y = c[1]*f1+p[1]*f0;

            segments.push([p1x,p1y,p2x,p2y]);
        }

        for(let idx=0; idx<points.length;idx++) {
            let c = points[idx];
            let s0 = segments[idx];

            let s1 = segments[(idx+1) % segments.length];


            if (idx == 0) {
                s += " M " + s0[0] + "," + s0[1];
            }
            s += " L " + s0[2] + "," + s0[3];
            s += " Q " + c[0] + "," + c[1] + " " + s1[0]+","+s1[1];
        }
        s += " Z";
        return s;
    }

    compute_standard_path(points) {
        let sep = '';
        let s = '';
        let action = "M";
        for(let idx=0; idx<points.length; idx++) {
            s += action;
            let p = points[idx];
            let x = p[0];
            let y = p[1];
            s += sep;
            sep = ' ';
            s += x+" "+y;
            action = "L";
        }
        s += " Z";
        return s;
    }

    compute_sector_path(ox,oy,r1,r2,theta1,theta2) {
        let s = "M"+(ox+r2*Math.cos(theta1))+","+(oy+r2*Math.sin(theta1))+" ";
        let x = 0;
        if ((theta2-theta1) > Math.PI) {
            x = 1;
        }
        s += "A"+r2+","+r2+","+"0,"+x+",1,"+(ox+r2*Math.cos(theta2))+","+(oy+r2*Math.sin(theta2))+" ";
        s += "L"+(ox+r1*Math.cos(theta2))+","+(oy+r1*Math.sin(theta2))+" ";
        s += "A"+r1+","+r1+","+"0,"+x+",0,"+(ox+r1*Math.cos(theta1))+","+(oy+r1*Math.sin(theta1))+" ";
        s += "z";
        return s;
    }

    compute_triangle_path(cx,cy,angle,radius) {
        let ax1 = cx + radius*Math.cos(angle);
        let ay1 = cy + radius*Math.sin(angle);
        let ax2 = cx + radius*Math.cos(angle-Math.PI*2/3);
        let ay2 = cy + radius*Math.sin(angle-Math.PI*2/3);
        let ax3 = cx + radius*Math.cos(angle+Math.PI*2/3);
        let ay3 = cy + radius*Math.sin(angle+Math.PI*2/3);
        return "M"+ax1+","+ay1 + " L"+ax2+","+ay2+ " L"+ax3+","+ay3+" z";
    }
}

/* src/js/common/palette.js */

var skadi = skadi || {};

skadi.Palette = class {

  constructor(design, id, closecb) {
    this.design = design;
    this.allitems = [];
    let nodeTypes = this.design.get_schema().get_node_types();
    for (let idx in nodeTypes) {
      let tid = nodeTypes[idx];
      let type = this.design.get_schema().get_node_type(tid);
      let entry = new skadi.PaletteEntry(this, design, type);
      this.allitems.push(entry);
    }
    this.id = id;
    this.closecb = closecb;
    this.width = 550;
  }

  open() {
    let x = 100;
    let y = 200;
    this.dial = new skadi.PaletteDialogue("node_palette", this.design, "Palette", x, y, 500, 500, this.closecb, function(){},true, false, true);

    for(let idx=0; idx<this.allitems.length; idx++) {
      this.dial.add(this.allitems[idx]);
    }
    this.dial.open();
  }

  intersects_window(x,y) {
    if (this.dial) {
        let pos = this.dial.get_position();
        if (x > pos.x && x < (pos.x+pos.w) && y > pos.y && y < (pos.y+pos.h)) {
            return true;
        }
    }
    return false;
  }
}

/* src/js/common/palette_entry.js */

var skadi = skadi || {};

skadi.PaletteEntry = class {

  constructor(palette, design, node_type) {
    this.palette = palette;
    this.design = design;
    this.node_type = node_type;
    this.node = null;
    this.overlay_node = null;
    this.overlay_grp = null;
    this.overlay_x = 0;
    this.overlay_y = 0;
    this.width = 220;
    this.height = 180;
    this.x = 110;
    this.y = 90;
    this.metadata = {
      "name": this.node_type.get_name(),
      "description": this.node_type.get_description()
    };
  }

  get_size(w,h) {
    return {"width":this.width, "height":this.height};
  }

  draw(grp) {
    let core_node = new skadi.CoreNode(this.design.core,this.node_type, this.design.next_id("nl"), this.x, this.y, this.metadata);
    this.node = new skadi.Node(this.design, core_node, false);
    this.node.draw(grp);
    
    this.overlay_grp = this.design.get_skadi_svg_dialogue_group();
    this.node.set_drag_handlers(
      (evloc) => {
        this.overlay_x = evloc.x;
        this.overlay_y = evloc.y;
        let core_node = new skadi.CoreNode(this.design.core, this.node_type, this.design.next_id("nl"),
          this.overlay_x, this.overlay_y, this.metadata);
        this.overlay_node = new skadi.Node(this.design, core_node, false);
        this.overlay_node.set_display_tooltips(false);
        this.overlay_node.draw(this.overlay_grp);
      },
      (evloc) => {
        this.overlay_x = evloc.x;
        this.overlay_y = evloc.y;
        this.overlay_node.update_position(this.overlay_x, this.overlay_y);
      },
      async (evloc) => {
        if (this.overlay_node) {
          this.overlay_node.remove();
          this.overlay_node = null;
          if (!this.palette.intersects_window(evloc.x,evloc.y)) {
              let tx = this.overlay_x - this.design.offset_x;
              let ty = this.overlay_y - this.design.offset_y;
              let cc = this.design.to_canvas_coords(tx,ty);
              await this.design.create_node(null,this.node_type, cc.x, cc.y,this.metadata,{}, false);
          }
        }
      });
    
    return {};
  }

  remove() {
    if (this.node) {
      this.node.remove();
      this.node = null;
    }
  }

  get_package_id() {
    if (this.node_type) {
      return this.node_type.get_package_id();
    }
    return "";
  }

  get_group() {
    return this.node.get_group();
  }

  get_id() {
    return this.node_type.get_id();
  }
}




/* src/js/dialogs/svg_dialogue.js */

var skadi = skadi || {};

skadi.SvgDialogue = class {

  constructor(id, design, title, x, y, content_width, content_height, close_handler, resize_handler, fullscreen_handler, scrollable, auto_close, draggable, draw_callback, draw_on_canvas) {
    this.design = design;
    this.title = title;

    this.close_handler = close_handler;
    this.resize_handler = resize_handler;
    this.fullscreen_handler = fullscreen_handler;
    this.draw_callback = draw_callback;

    this.draw_on_canvas = draw_on_canvas;

    this.id = id;
    this.x = x;
    this.y = y;
    this.header_sz = 40;
    this.footer_sz = 40;
    this.padding = 5;

    this.width = content_width + 2*this.padding;
    this.height = content_height + 2*this.padding + this.header_sz;

    this.startx = 0;
    this.starty = 0;
    this.ox = 0;
    this.oy = 0;

    this.dragging = false;
    this.remove_on_close = true;
    this.draggable = draggable;

    this.content_offset = 0;

    let that = this;
    this.close_btn = new skadi.Button(this.design, this.width - this.header_sz / 2, this.header_sz / 2, this.header_sz, this.header_sz, skadi.icon_close_purple, function () {
      that.close();
    });
    if (this.resize_handler) {
      this.resize_btn = new skadi.Button(this.design, this.x + this.width - this.footer_sz / 2, this.y + this.height - this.footer_sz / 2, this.footer_sz, this.footer_sz, skadi.icon_drag_indicator_purple, function () {
      });
    } else {
      this.resize_btn = null;
    }
    if (this.fullscreen_handler) {
      this.fullscreen_btn = new skadi.Button(this.design, this.width - this.header_sz - this.header_sz / 2, this.header_sz / 2, this.header_sz, this.header_sz, skadi.icon_fullscreen_purple, () => {
        this.fullscreen_handler();
      });
    } else {
      this.fullscreen_btn = null;
    }

    this.compute_content_size();

    this.compute_content_size();

    this.auto_close = auto_close;
    this.connector_grp = null;
    this.connector_path = null;
    this.parent_node = null;
    this.movement_listener = null;
  }

  set_parent_node(node) {
    this.parent_node = node;
    let container = this.design.get_node_connector_group();

    this.connector_grp = container.append("g").attr("id", this.id).attr("class", "link");

    this.connector_path = this.connector_grp.append("path")
      .attr("id", this.id + "path")
      .attr("class", "dialog_connector");

    this.connector_path.attr("stroke", "gray");

    this.connector_pad = this.connector_grp.append("circle")
        .attr("r","10").attr("class","dialog_connector_pad");

    this.movement_listener = () => {
        this.update_connector();
    };
    this.parent_node.add_movement_listener(this.movement_listener);
    this.update_connector();

    this.design.core.add_design_event_handler("panzoom",this.movement_listener);
  }

  get_parent_node() {
    return this.parent_node;
  }

  update_connector() {

    if (this.connector_path) {

      let c = { "x":this.parent_node.get_x(), "y": this.parent_node.get_y() };
      let c2 = this.draw_on_canvas ? { "x":this.x + this.width/2, "y": this.y }
        : { "x":this.x + this.width/2, "y": this.y - this.design.offset_y };
      let c3 = this.draw_on_canvas ? c2 : this.design.to_canvas_coords(c2.x,c2.y);
      let c4 = { "x":this.x + this.width/2, "y": this.y-this.design.offset_y - 75 };
      let c5 = this.draw_on_canvas ? c4 : this.design.to_canvas_coords(c4.x,c4.y);

      const path =  "M"+c.x+","+c.y+" Q "+c5.x + " " + c5.y +", " + c3.x + " " + c3.y;

      this.connector_path.attr("d", path);
      this.connector_pad.attr("cx", c3.x);
      this.connector_pad.attr("cy", c3.y);
    }
  }

  get_size() {
    return {"width":this.width,"height":this.height};
  }

  set_content_size(content_width, content_height) {
    this.content_width = content_width;
    this.content_height = content_height;
    this.width = this.content_width + 2*this.padding;
    this.height = this.content_height + 2*this.padding + this.header_sz;
    if (this.resize_btn) {
      this.height += this.footer_sz;
    }
    this.apply_size(true);
  }

  compute_content_size() {
    this.content_width = this.width - 2*this.padding;
    this.content_height = this.height - 2*this.padding - this.header_sz;
    if (this.resize_btn) {
      this.content_height -= this.footer_sz;
    }
    if (this.content_height < 0) {
      this.content_height = 0;
    }

    if (this.resize_handler) {
      this.content_depth = this.resize_handler(this.content_width, this.content_depth, false);
    }
  }

  open() {

    let container = this.draw_on_canvas ? this.design.get_canvas_dialogue_group() : this.design.get_skadi_svg_dialogue_group();

    this.grp = container.append("g");

    this.clip = this.grp.append("clipPath").attr("id",this.id);
    this.clip_rect = this.clip.append("rect")
      .attr("x",this.padding)
      .attr("y",this.header_sz+this.padding)
      .attr("width",this.content_width)
      .attr("height",this.content_height);

    this.dial = this.grp.append("rect")
      .attr("x",0)
      .attr("y",0)
      .attr("rx",5)
      .attr("ry",5)
      .attr("width",this.width)
      .attr("height",this.height)
      .attr("class","svg_dialogue");

    this.header = this.grp.append("rect")
      .attr("x",0)
      .attr("y",0)
      .attr("rx",5)
      .attr("ry",5)
      .attr("width",this.width)
      .attr("height",this.header_sz)
      .attr("class","svg_dialogue_header");

    this.title = this.grp.append("text").attr("x",this.padding).attr("y",this.header_sz/2)
      .attr("class","svg_dialogue_title")
      .attr("dominant-baseline","middle")
        .style("pointer-events","none")
      .text(this.title);

    this.content_clip = this.grp.append("g").attr("clip-path","url(#"+this.id+")");
    this.content_grp = this.content_clip.append("g");
    this.content_grp.ignore_mouse_events();

    this.divider1 = this.grp.append("line").attr("x1",0).attr("y1",this.header_sz).attr("x2",this.width).attr("y2",this.header_sz).attr("class","svg_dialogue_divider");
    if (this.resize_btn) {
      this.divider2 = this.grp.append("line").attr("x1",0).attr("y1",this.height-this.footer_sz).attr("x2",this.width).attr("y2",this.height-this.footer_sz).attr("class","svg_dialogue_divider");
    } else {
      this.divider2 = null;
    }

    let that = this;

    this.update();

    if (this.draggable) {
      let drag =  skadi.x3.drag();
      drag
          .on("start", function () {
          })
          .on("drag", function (x, y) {
            that.drag_move(x, y);
          })
          .on("end", function (x, y) {
            that.end_drag_move(x, y);
          });

      this.header.call(drag);
    }

    this.close_btn.draw(this.grp);
    if (this.fullscreen_btn) {
      this.fullscreen_btn.draw(this.grp);
    }

    this.draw_callback(this.content_grp);

    if (this.resize_btn) {
      this.resizegrp = container.append("g");
      this.resize_btn.draw(this.resizegrp);

      let resize =  skadi.x3.drag();
      resize
        .on("start", function() {
        })
        .on("drag", function(x,y) {
          that.drag_resize(x, y);
        })
        .on("end", function(x,y) {
          that.end_drag_resize(x,y);
        });

      this.resizegrp.call(resize);
    } else {
      this.resizegrp = null;
    }

    this.opened = true;

    if (this.auto_close) {
      this.grp.node().onmouseleave = function() {
        that.close();
      }
    }
  }

  drag_move(x, y) {
    if (!this.dragging) {
      this.startx = x;
      this.starty = y;
      this.ox = this.x;
      this.oy = this.y;
      this.dragging = true;
    } else {
      let dx = x - this.startx;
      let dy = y - this.starty;
      if (this.draw_on_canvas) {
        dx /= this.design.current_zoom;
        dy /= this.design.current_zoom;
      }
      this.update_position(this.ox + dx, this.oy + dy);
      this.update_connector();
    }
  }

  end_drag_move(x, y) {
    if (this.dragging) {
      this.dragging = false;
      let dx = x - this.startx;
      let dy = y - this.starty;
      if (this.draw_on_canvas) {
        dx /= this.design.current_zoom;
        dy /= this.design.current_zoom;
      }
      this.update_position(this.ox + dx, this.oy + dy);
      this.update_connector();
    }
  }

  drag_resize(x, y) {
    if (!this.dragging) {
      this.startx = x;
      this.starty = y;
      this.ow = this.width;
      this.oh = this.height;
      this.dragging = true;
    } else {
      let dx = x - this.startx;
      let dy = y - this.starty;
      if (this.draw_on_canvas) {
        dx /= this.design.current_zoom;
        dy /= this.design.current_zoom;
      }
      this.update_size(this.ow + dx, this.oh + dy, false);
    }
  }

  end_drag_resize(x, y) {
    if (this.dragging) {
      this.dragging = false;
      let dx = x - this.startx;
      let dy = y - this.starty;
      if (this.draw_on_canvas) {
        dx /= this.design.current_zoom;
        dy /= this.design.current_zoom;
      }
      this.update_size(this.ow + dx, this.oh + dy, true);
    }
  }

  update_size(w,h,is_final) {
    this.width = w;
    this.height = h;

    this.compute_content_size();
    this.apply_size(is_final);
  }

  apply_size(is_final) {

    this.dial
      .attr("width",this.width)
      .attr("height",this.height);

    this.header
        .attr("width",this.width);

    this.clip_rect
    .attr("width",this.content_width)
    .attr("height",this.content_height);

    this.divider1
      .attr("x2",this.width);

    if (this.divider2) {
      this.divider2
        .attr("x2",this.width)
        .attr("y1",this.height-this.header_sz)
        .attr("y2",this.height-this.header_sz);
    }

    this.close_btn.update_position(this.width-this.header_sz/2,this.header_sz/2);
    if (this.resize_btn) {
      this.resize_btn.update_position(this.x+this.width-this.header_sz/2,this.y+this.height-this.header_sz/2);
    }
    if (this.fullscreen_btn) {
      this.fullscreen_btn.update_position(this.width-this.header_sz - this.header_sz/2,this.header_sz/2);
    }
    if (this.resize_handler) {
      this.resize_handler(this.content_width,this.content_height,is_final);
    }
    this.update_connector();
  }

  close() {
    if (this.remove_on_close) {
      this.grp.remove();
      if (this.connector_grp) {
        this.connector_grp.remove();
        this.connector_grp = null;
      }
      if (this.resizegrp) {
        this.resizegrp.remove();
      }
      if (this.movement_listener) {
        this.parent_node.remove_movement_listener(this.movement_listener);
        this.design.core.remove_design_event_handler("panzoom",this.movement_listener);
        this.movement_listener = null;
      }
    }
    if (this.close_handler) {
      this.close_handler(this.dial);
    }
  }

  update_position(x, y) {
    this.x = x;
    this.y = y;
    this.update();
  }

  get_position() {
    return {
      "x": this.x,
      "y": this.y,
      "w": this.width,
      "h": this.height
    };
  }

  update() {
    let transform = "translate(" + this.x + "," + this.y + ")";
    this.grp
      .attr("transform", transform);
    if (this.resize_btn) {
      this.resize_btn.update_position(this.x+this.width-20,this.y+this.height-20);
    }
  }

  pack() {
  }
}


/* src/js/dialogs/iframe_dialogue.js */

var skadi = skadi || {};

skadi.IFrameDialogue = class extends skadi.SvgDialogue {

  constructor(id, design, title, x, y, content_width, content_height, close_handler, can_resize, url, open_callback, resize_callback, fullscreen_callback, draw_on_canvas) {
    super(id, design, design.localise(title), x, y, content_width, content_height, close_handler,
        function(width,height,is_final) {
          return this.resize(width,height,is_final);
          },
        fullscreen_callback,
        false, false, true,
        function(grp) {
          this.draw(grp);
        }, draw_on_canvas);
    this.design = design;
    this.url = url;
    this.open_callback = open_callback;
    this.resize_callback = resize_callback;
  }

  draw(grp) {
    this.fo = grp.append("foreignObject").attr("x",this.padding+"px").attr("y",(this.header_sz+this.padding)+"px").attr("width",this.content_width+"px").attr("height",this.content_height+"px");
    this.content = null;
    if (this.url) {
      this.content = this.fo.append("iframe",false).attr("class", "skadi_iframe").attr("src", this.url).attr("sandbox","allow-scripts").attr("width", this.content_width + "px").attr("height", this.content_height + "px").attr("target","_new");
    } else {
      this.content = this.fo.append("div",false).attr("width", this.content_width + "px").attr("height", this.content_height + "px");
      if (this.open_callback) {
        this.open_callback(this.content.node(),() => this.close());
        this.resize(this.content_width,this.content_height,true);
      }
    }
  }

  resize(w,h,is_final) {
    if (this.fo) {
      this.fo.attr("width", w + "px").attr("height", h + "px");
      this.content.attr("width", w + "px").attr("height", h + "px");
    }
    if (is_final && this.resize_callback) {
      this.resize_callback(w,h);
    }
    return this.content_height;
  }
}



/* src/js/dialogs/palette_dialogue.js */

var skadi = skadi || {};

skadi.PaletteDialogue = class extends skadi.IFrameDialogue {

    static html() {
        return `<div>
        <div class="exo-row" id="palette_controls">
            <div class="exo-2-cell">
                <exo-text id="palette_filter" label="{{palette.filter_by}}">
            </div>
            <div style="margin-top:10px;">
                {{palette.pages}}:
                <div id="page_buttons"></div>
             </div>
        </div>
        <div id="palette_content">
        </div>
        </div>`
    }

    constructor(id, design, title, x, y, width, height, closeHandler, resize_handler, scrollable, autoClose, draggable) {
        super(id, design, title, x, y, width, height, closeHandler,
            true,
            null,
            function (elt) {
                this.show(elt);
            },
            function (width, height, is_final) {
                this.current_page = 1;
                this.page_height = height;
                this.refresh_view();
            }, null, false);

        this.all_entries = [];
        this.entries = [];
        this.entry_tiles = {};
        this.current_page = 1;
        this.palette_controls = null;
        this.page_button_container = null;
        this.page_buttons = {};
        this.palette_filter = null;
        this.filter_text = "";
    }

    add(entry) {
        this.all_entries.push(entry);
    }

    show(elt) {
        elt.innerHTML = this.design.localise(skadi.PaletteDialogue.html());
        this.palette_controls = skadi.$("palette_controls");
        this.page_button_container = skadi.$("page_buttons");
        this.content_elt = skadi.$("palette_content");
        this.palette_filter = skadi.$("palette_filter");
        this.palette_filter.addEventListener("change", (evt) => {
            this.filter_text = evt.target.value;
            this.refresh_view();
        });
    }

    has_tile(entry) {
        return (entry.get_id() in this.entry_tiles);
    }

    get_tile(entry) {
        if (entry.get_id() in this.entry_tiles) {
            return this.entry_tiles[entry.get_id()];
        }
        let sz = entry.get_size();

        let elt_sel = new skadi.X3Selection([this.content_elt]);
        let tile = elt_sel.append("div");
        let svg = tile.append("svg")
            .attr("width", sz.width)
            .attr("height", sz.height);

        let group = svg.append("g").attr("id", "viewport");
        entry.draw(group);
        this.entry_tiles[entry.get_id()] = tile;
        return tile;
    }

    matches(entry) {
        if (!this.filter_text) {
            return true;
        }
        let search_keys = ["name", "description"];
        let matched = false;
        search_keys.forEach(key => {
            if (entry.metadata[key].toLowerCase().includes(this.filter_text.toLowerCase())) {
                matched = true;
            }
        });
        return matched;
    }

    refresh_view() {
        let r = this.content_elt.getBoundingClientRect();
        let w = r.width;
        r = this.palette_controls.getBoundingClientRect();
        let h = this.page_height - r.height;
        this.entries = [];
        this.all_entries.forEach(entry => {
            if (this.matches(entry)) {
                this.entries.push(entry);
            }
        });
        if (this.entries.length) {
            let entry = this.entries[0];
            let sz = entry.get_size();
            let cols = Math.floor(w / sz.width);
            let rows = Math.floor(h / sz.height);
            if (cols < 1) {
                cols = 1;
            }
            if (rows < 1) {
                rows = 1;
            }
            this.page_size = cols * rows;
            this.page_count = Math.ceil(this.entries.length / this.page_size);
            this.current_page = 1;
        } else {
            this.page_size = 0;
            this.page_count = 0;
            this.current_page = 0;
        }
        for (let page_nr = 1; page_nr <= this.page_count; page_nr += 1) {
            if (!(page_nr in this.page_buttons)) {
                let page_btn = document.createElement("input");
                page_btn.setAttribute("type", "button");
                page_btn.setAttribute("class", "palette_page_button");
                page_btn.setAttribute("value", "" + page_nr);
                page_btn.addEventListener("click", this.make_page_callback(page_nr));
                this.page_buttons[page_nr] = new skadi.X3Selection([page_btn]);
                this.page_button_container.appendChild(page_btn);
            }
        }
        if (this.current_page > this.page_count) {
            this.current_page = this.page_count;
        }
        this.draw_page();
    }

    make_page_callback(page_nr) {
        return () => {
            this.current_page = page_nr;
            this.draw_page();
        }
    }

    draw_page() {
        let page_start = (this.current_page - 1) * this.page_size;
        let page_end = page_start + this.page_size;
        this.all_entries.forEach(entry => {
            if (!this.entries.includes(entry)) {
                if (this.has_tile(entry)) {
                    let tile = this.get_tile(entry);
                    tile.style("display", "none");
                }
            }
        });
        for (let idx = 0; idx < this.entries.length; idx++) {
            let tile = this.get_tile(this.entries[idx]);
            if (idx < page_start || idx >= page_end) {
                tile.style("display", "none");
            } else {
                tile.style("display", "inline-block");
            }
        }
        for (let page_nr in this.page_buttons) {
            let page_btn = this.page_buttons[page_nr];
            if (page_nr > this.page_count) {
                page_btn.style("display", "none");
            } else {
                if (page_nr == this.current_page) {
                    page_btn.attr("class", "palette_page_button selected_palette_page_button");
                } else {
                    page_btn.attr("class", "palette_page_button");
                }
                page_btn.style("display", "inline-block");
            }
        }
    }
}

