/* src/js/core/core.js */

var skadi = skadi || {};

skadi.Core = class {

    constructor(id, l10n_utils, schema, instance_factory, topology_store, platform_extensions) {
        this.id = id;
        this.l10n_utils = l10n_utils;
        this.instance_factory = instance_factory;
        this.topology_store = topology_store;

        this.user_settings = new skadi.UserSettings();

        this.schema = schema;

        this.node_event_handlers = {};
        this.link_event_handlers = {};
        this.design_event_handlers = {};
        this.configuration_event_handlers = {};

        this.node_status_event_handlers = { "status":[]};
        this.configuration_status_event_handlers = { "status":[]};
        this.node_statuses = {};
        this.configuration_statuses = {};

        this.node_execution_event_handlers = { "state": [] };
        this.node_execution_states = {};
        this.node_execution_manual_tracking = {};  // node_id => true if execution state is being explictly set by the user

        this.network = new skadi.Network({});

        this.metadata = {
            "description": "",
            "authors": "",
            "version": "0.1"
        }

        this.language = "en";
        this.deserialising = false;
        this.platform_extensions = platform_extensions || [];
    }


    async init() {
        // load up configurations for any packages that specify them
        for(let package_id in this.schema.package_types) {
            let package_type = this.schema.package_types[package_id];
            if (package_type.get_configuration_classname()) {
                await this.create_configuration(package_type);
            }
        }
    }

    async create_configuration(package_type) {
        let conf = new skadi.CoreConfiguration(this,package_type);
        let wrapper = await this.create_configuration_instance(conf);
        conf.set_wrapper(wrapper);
        this.add_configuration(conf);
    }

    async create_configuration_instance(core_configuration) {
        let service = new skadi.ConfigurationService(core_configuration);
        let wrapper = new skadi.Wrapper(core_configuration, "configuration", service, core_configuration.package_type.get_l10n_utils());
        await wrapper.load();
        service.wrapper = wrapper;
        let classname = core_configuration.package_type.get_configuration_classname();
        let instance = await this.instance_factory.create_configuration_instance(core_configuration.get_id(),core_configuration.get_package_type().get_configuration_classname(),service);
        wrapper.set_instance(instance);
        return wrapper;
    }

    get_id() {
        return this.id;
    }

    set_id(id) {
        this.id = id;
    }

    get_language() {
        return this.language;
    }

    set_language(language) {
        this.language = language;
    }

    get_user_setting(name) {
        return this.user_settings.get_setting(name);
    }

    set_user_setting(name,value) {
        this.user_settings.set_setting(name,value);
    }

    localise(text) {
        return this.l10n_utils.localise(text);
    }

    get_schema() {
        return this.schema;
    }

    get_l10n_utils() {
        return this.l10n_utils;
    }

    alert(message, status_state) {
        new skadi.Alert(message, status_state);
    }

    /* Access the topology store and node/configuration factories */

    get_topology_store() {
        return this.topology_store;
    }

    get_network() {
        return this.network;
    }

    /* event handling - add/remove event handlers */

    add_configuration_event_handler(configuration_event_type, callback) {
        this.add_event_handler(this.configuration_event_handlers, configuration_event_type, callback);
        return callback;
    }

    remove_configuration_event_handler(configuration_event_type, callback) {
        this.remove_event_handler(this.configuration_event_handlers, configuration_event_type, callback);
    }

    add_node_event_handler(node_event_type, callback) {
        this.add_event_handler(this.node_event_handlers, node_event_type, callback);
        return callback;
    }

    remove_node_event_handler(node_event_type, callback) {
        this.remove_event_handler(this.node_event_handlers, node_event_type, callback);
    }

    add_link_event_handler(link_event_type, callback) {
        this.add_event_handler(this.link_event_handlers, link_event_type, callback);
        return callback;
    }

    remove_link_event_handler(link_event_type, callback) {
        this.remove_event_handler(this.link_event_handlers, link_event_type, callback);
    }

    add_design_event_handler(design_event_type, callback) {
        this.add_event_handler(this.design_event_handlers, design_event_type, callback);
        return callback;
    }

    remove_design_event_handler(design_event_type, callback) {
        this.remove_event_handler(this.design_event_handlers, design_event_type, callback);
    }

    add_node_status_event_handler(callback) {
        this.add_event_handler(this.node_status_event_handlers, "status", callback);
        for(let node_id in this.node_statuses) {
            let status = this.node_statuses[node_id];
            callback(node_id, status.state, status.message);
        }
        return callback;
    }

    remove_node_status_event_handler(callback) {
        this.remove_event_handler(this.node_status_event_handlers, "status", callback);
    }

    add_configuration_status_event_handler(callback) {
        this.add_event_handler(this.configuration_status_event_handlers, "status", callback);
        for(let package_id in this.configuration_statuses) {
            let status = this.configuration_statuses[package_id];
            callback(package_id, status.state, status.message);
        }
        return callback;
    }

    remove_configuration_status_event_handler(callback) {
        this.remove_event_handler(this.configuration_status_event_handlers, "status", callback);
    }

    add_node_execution_event_handler(callback) {
        this.add_event_handler(this.node_execution_event_handlers, "state", callback);
        for(let node_id in this.node_execution_states) {
            callback(node_id, this.node_execution_states[node_id]);
        }
        return callback;
    }

    remove_node_execution_event_handler(callback) {
        this.remove_event_handler(this.node_execution_event_handlers, "state", callback);
    }

    add_event_handler(listeners, event_type, callback) {
        if (!(event_type in listeners)) {
            listeners[event_type] = [];
        }
        listeners[event_type].push(callback);
    }

    remove_event_handler(listeners, event_type, callback) {
        if (!(event_type in listeners)) {
            return;
        }
        listeners[event_type] = listeners[event_type].filter(c => c != callback);
    }

    /* event firing */

    fire_node_event(node_event_type, node_id, suppress_callbacks) {
        let node = this.get_node(node_id);
        if (node_event_type in this.node_event_handlers) {
            let callbacks = this.node_event_handlers[node_event_type];
            for (let idx = 0; idx < callbacks.length; idx++) {
                let callback = callbacks[idx];
                if (!suppress_callbacks || !suppress_callbacks.includes(callback)) {
                    if (node_event_type == "add") {
                        callback(node_id, node.get_type().get_id(), node.get_position(), node.get_metadata());
                    } else if (node_event_type == "remove") {
                        callback(node_id, node.get_type().get_id());
                    } else if (node_event_type == "update_position") {
                        callback(node_id, node.get_position());
                    } else if (node_event_type == "update_metadata") {
                        callback(node_id, node.get_metadata());
                    }
                }
            }
        }
    }

    fire_link_event(link_event_type, link_id, suppress_callbacks) {
        let link = this.get_link(link_id);
        let link_type = link.get_link_type().get_id();
        let from_node_id = link.get_from_node().get_id();
        let from_port_name = link.get_from_port_name();
        let to_node_id = link.get_to_node().get_id();
        let to_port_name = link.get_to_port_name();
        if (link_event_type in this.link_event_handlers) {
            let callbacks = this.link_event_handlers[link_event_type];
            for (let idx = 0; idx < callbacks.length; idx++) {
                if (!suppress_callbacks || !suppress_callbacks.includes(callbacks[idx])) {
                    callbacks[idx](link_id, link_type, from_node_id, from_port_name, to_node_id, to_port_name);
                }
            }
        }
    }

    fire_design_event(design_event_type, aux_info, suppress_callbacks) {
        if (design_event_type in this.design_event_handlers) {
            let callbacks = this.design_event_handlers[design_event_type];
            for (let idx = 0; idx < callbacks.length; idx++) {
                if (!suppress_callbacks || !suppress_callbacks.includes(callbacks[idx])) {
                    callbacks[idx](aux_info);
                }
            }
        }
    }

    fire_node_status_event(node_id, status_state, status_message, suppress_callbacks) {
        let callbacks = this.node_status_event_handlers["status"];
        for (let idx = 0; idx < callbacks.length; idx++) {
            if (!suppress_callbacks || !suppress_callbacks.includes(callbacks[idx])) {
                callbacks[idx](node_id, status_state, status_message);
            }
        }
    }

    fire_configuration_status_event(package_id, status_state, status_message, suppress_callbacks) {
        let callbacks = this.configuration_status_event_handlers["status"];
        for (let idx = 0; idx < callbacks.length; idx++) {
            if (!suppress_callbacks || !suppress_callbacks.includes(callbacks[idx])) {
                callbacks[idx](package_id, status_state, status_message);
            }
        }
    }

    fire_node_execution_event(node_id, suppress_callbacks) {
        let callbacks = this.node_execution_event_handlers["state"];
        let execution_state = this.node_execution_states[node_id] || skadi.NodeExecutionStates.pending;
        for (let idx = 0; idx < callbacks.length; idx++) {
            if (!suppress_callbacks || !suppress_callbacks.includes(callbacks[idx])) {
                callbacks[idx](node_id, execution_state);
            }
        }
    }

    /* execution related */

    execution_complete() {
        this.network.get_node_list().forEach(node_id => {
            this.fire_node_execution_event(node_id);
        });
    }

    /* configuration related */

    get_configuration(package_id) {
        return this.network.get_configuration(package_id);
    }

    add_configuration(configuration) {
        this.network.add_configuration(configuration);
    }

    update_configuration_status(package_id, state, status_message) {
        this.configuration_statuses[package_id] = { "state":state, "message":status_message }
        this.fire_configuration_status_event(package_id, state, status_message);
    }

    /* node related */

    async create_node(node_id, node_type, x, y, metadata, properties, suppress_callbacks) {
        x = Math.round(x / skadi.GRID_SNAP) * skadi.GRID_SNAP;
        y = Math.round(y / skadi.GRID_SNAP) * skadi.GRID_SNAP;
        let id = node_id || this.next_id("n");
        let node = new skadi.CoreNode(this, node_type, id, x, y, metadata, properties);
        let wrapper = await this.create_node_instance(node);
        node.wrapper = wrapper;
        this.add_node(node, suppress_callbacks);
        return id;
    }

    async create_node_instance(core_node) {
        let service = new skadi.NodeService(core_node);
        let wrapper = new skadi.Wrapper(core_node, "node", service, core_node.get_type().get_package_type().get_l10n_utils());
        await wrapper.load();
        service.wrapper = wrapper;
        let classname = core_node.get_type().get_classname();
        let instance = await this.instance_factory.create_node_instance(core_node.get_id(), core_node.get_type().get_id(), classname, service);
        wrapper.set_instance(instance);
        return wrapper;
    }

    add_node(node, suppress_callbacks) {
        let node_id = node.get_id();
        this.node_execution_states[node_id] = "pending";
        this.network.add_node(node);
        if (!this.deserialising) {
            // if deserialising, defer notifying nodes about connections until deserialisation is complete
            // and the connections are set up
            this.notify_connections_changed(node_id);
        }
        this.fire_node_event("add",node_id, suppress_callbacks);
    }

    remove_node(node_id, suppress_callbacks) {
        // cascade to any links connected to the node to remove
        let removed_link_ids = [];
        for (let link_id in this.network.links) {
            let link = this.network.get_link(link_id);
            if (link.get_from_node().get_id() === node_id|| link.get_to_node().get_id() === node_id) {
                let link_id = link.get_id();
                if (!removed_link_ids.includes(link_id)) {
                    removed_link_ids.push(link_id);
                }
            }
        }
        for (let idx = 0; idx < removed_link_ids.length; idx++) {
            this.remove_link(removed_link_ids[idx]);
        }
        let node = this.get_node(node_id);
        node.remove();
        this.fire_node_event("remove", node_id, suppress_callbacks);

        if (node_id in this.node_statuses) {
            delete this.node_statuses[node_id];
        }
        if (node_id in this.node_execution_states) {
            delete this.node_execution_states[node_id];
        }
        if (node_id in this.node_execution_manual_tracking) {
            delete this.node_execution_manual_tracking[node_id];
        }

        this.network.remove(node_id);
        return removed_link_ids;
    }

    get_node_ids() {
        return this.network.get_node_list();
    }

    get_node(node_id) {
        return this.network.get_node(node_id);
    }

    update_node_status(node_id, state, status_message) {
        this.node_statuses[node_id] = {"state":state, "message":status_message };
        this.fire_node_status_event(node_id, state, status_message);
    }

    update_execution_state(node_id, state, is_manual) {
        if (is_manual) {
            this.node_execution_manual_tracking[node_id] = true;
        }
        if (is_manual || (!is_manual && !(node_id in this.node_execution_manual_tracking))) {
            this.node_execution_states[node_id] = state;
            this.fire_node_execution_event(node_id);
        }
    }

    update_metadata(node_id, metadata) {
        let node = this.network.get_node(node_id);
        if (node) {
            node.update_metadata(metadata);
        }
        this.fire_node_event("update_metadata", node.get_id(), null);
    }

    update_design_metadata(metadata) {
        for(let key in metadata) {
            this.metadata[key] = metadata[key];
        }
        this.fire_design_event("update_design_metadata", this.metadata, null);
    }

    get_design_metadata() {
        let copy = {};
        for(let key in this.metadata) {
            copy[key] = this.metadata[key];
        }
        return copy;
    }

    get_connected_nodes(node_id, port_name, is_input_port) {
        let connected_nodes = is_input_port ? this.network.get_connected_input_nodes(node_id, port_name): this.network.get_connected_output_nodes(node_id, port_name);
        let connected_wrappers = [];
        for(let i=0; i<connected_nodes.length; i++) {
            let wrapper = connected_nodes[i][0].get_wrapper();
            let connected_port = connected_nodes[i][1];
            connected_wrappers.push([wrapper,connected_port]);
        }
        return connected_wrappers;
    }

    fire_output_port_event(from_node_id, output_port_name, event_type, event_value) {
        let connected_nodes = this.get_connected_nodes(from_node_id, output_port_name, false);
        for(let idx=0; idx<connected_nodes.length; idx++) {
            let node_wrapper = connected_nodes[idx][0];
            let input_port_name = connected_nodes[idx][1];
            node_wrapper.handle_input_port_event(input_port_name, event_type, event_value);
        }
    }

    /* link/port related */

    create_link(from_node_id, from_port_name, to_node_id, to_port_name, link_type_id, link_id, suppress_callbacks) {
        let id = link_id || this.next_id("l");
        let link_type = this.schema.get_link_type(link_type_id);
        let from_node = this.get_node(from_node_id);
        let to_node = this.get_node(to_node_id);
        let link = new skadi.CoreLink(this, id, from_node, from_port_name, link_type, to_node, to_port_name);
        this.add_link(link, suppress_callbacks);
        return id;
    }

    add_link(link, suppress_callbacks) {
        let link_id = link.get_id();
        this.network.add_link(link, suppress_callbacks);
        if (!this.deserialising) {
            this.notify_connections_changed(link.get_from_node().get_id());
            this.notify_connections_changed(link.get_to_node().get_id());
        }
        this.fire_link_event("add", link_id, suppress_callbacks);
    }

    remove_link(link_id, suppress_callbacks) {
        let link = this.get_link(link_id);
        link.remove();
        this.fire_link_event("remove", link_id, suppress_callbacks);
        this.network.remove(link.get_id());

        this.notify_connections_changed(link.get_from_node().get_id());
        this.notify_connections_changed(link.get_to_node().get_id());
    }

    get_link(link_id) {
        return this.network.get_link(link_id);
    }

    get_link_ids() {
        return this.network.get_link_list();
    }

    notify_connections_changed(node_id) {
        let node = this.network.get_node(node_id);
        let connection_counts = this.network.get_connection_counts(node_id);
        node.notify_connections_changed(connection_counts);
    }

    /* design metadata */

    get_metadata() {
        return this.metadata;
    }

    /* provide unique IDs */

    next_id(prefix) {
        function fix(n,l) {
            let s = n.toString(16);
            if (s.length > l) {
                return s.slice(s.length-l,s.length);
            } else {
                while (s.length < l) {
                    s = "0" + s;
                }
                return s;
            }
        }
        while (true) {
            let id = prefix + fix(new Date().getTime(),6) + "_" + fix(Math.floor(Math.random()*256*256*256),6);
            if (!this.network.contains_id(id)) {
                return id;
            }
        }
    }

    /* clear the network */

    clear() {
        this.network.get_link_list().forEach(link_id => {
            let link = this.network.get_link(link_id);
            this.remove_link(link);
        })
        this.network.get_node_list().forEach(node_id => {
            let node = this.network.get_node(node_id);
            this.remove_node(node);
        });
        this.network.clear();
        this.fire_design_event("clear", null);
    }

    /* load/save */

    serialise() {
        let snodes = {};
        let slinks = {};

        let node_ids = this.network.get_node_list();
        for (let idx in node_ids) {
            let node_id = node_ids[idx];
            let node = this.network.get_node(node_id);
            if (!node.removed) { // check that this node is not marked as being removed
                snodes[node_id] = node.serialise();
            }
        }

        let link_ids = this.network.get_link_list();
        for (let idx in link_ids) {
            let link_id = link_ids[idx];
            let link = this.network.get_link(link_id);
            if (!link.removed) { // check that this link is not marked as being removed
                slinks[link_id] = link.serialise();
            }
        }

        let serialised = {"nodes": snodes, "links": slinks, "metadata": this.metadata };

        let configurations = {};
        this.network.get_configuration_list().forEach(package_id => {
            configurations[package_id] = this.network.get_configuration(package_id).serialise();
        });
        serialised["configurations"] = configurations;

        return serialised;
    }

    can_deserialise(from_obj) {

        // can we deserialise this topology?

        // check that the required packages and node types exist
        for (let node_id in from_obj.nodes) {
            let requested_node_type = from_obj.nodes[node_id]["node_type"];
            let requested_package_id = requested_node_type.split(":")[0];
            if (this.schema.get_package_type(requested_package_id) === undefined) {
                this.alert("Unable to load topology, package "+requested_package_id+" is not avaialble","error");
                return false;
            }
            if (this.schema.get_node_type(requested_node_type) === undefined) {
                this.alert("Unable to load topology, node type "+requested_node_type+" is not avaialble","error");
                return false;
            }
        }

        return true;
    }

    async deserialise(from_obj, node_renamings, suppress_callbacks) {

        if (!this.can_deserialise(from_obj)) {
            return;
        }

        this.fire_design_event("start_load",{},suppress_callbacks);

        this.deserialising = true;

        let link_renamings = {};

        let added_node_ids = [];
        let added_link_ids = [];

        for (let node_id in from_obj.nodes) {
            let obj = from_obj.nodes[node_id];
            if (node_id in node_renamings) {
                node_id = node_renamings[node_id];
            }
            let node = skadi.CoreNode.deserialise(this, node_id, obj);
            let wrapper = await this.create_node_instance(node);
            node.set_wrapper(wrapper);
            this.add_node(node, suppress_callbacks);
            added_node_ids.push(node_id);
        }

        for (let link_id in from_obj.links) {
            let obj = from_obj.links[link_id];
            if (this.network.has_link(link_id)) {
                let renamed_id = this.next_id("n");
                link_renamings[link_id] = renamed_id;
                link_id = renamed_id;
            }
            let link = skadi.CoreLink.deserialise(this, link_id, obj, node_renamings);
            this.add_link(link, suppress_callbacks);
            added_link_ids.push(link_id);
        }

        for(let idx=0; idx<added_node_ids.length; idx++) {
            let node_id = added_node_ids[idx];
            this.notify_connections_changed(node_id);
        }

        for(let key in from_obj["metadata"]) {
            this.metadata[key] = from_obj["metadata"][key];
        }

        this.deserialising = false;
        this.fire_design_event("end_load",{},suppress_callbacks);

        return {
            "added_node_ids": added_node_ids,
            "added_link_ids": added_link_ids
        }
    }

    close() {
        this.network.get_node_list().forEach(node_id => {
            this.network.get_node(node_id).close();
        });

        this.network.get_configuration_list().forEach(package_id => {
            this.network.get_configuration(package_id).close();
        });
    }
}




