var skadi = skadi || {};

skadi.CoreNode = class {

    constructor(core, node_type, id, x, y, metadata) {
        this.core = core;

        this.node_type = node_type;
        this.metadata = metadata;

        this.wrapper = null;

        this.id = id;

        // coordinates on canvas
        this.x = x;
        this.y = y;

        this.pages = this.node_type.pages;

        this.removed = false;
    }

    set_wrapper(wrapper) {
        this.wrapper = wrapper;
    }

    get_wrapper() {
        return this.wrapper;
    }

    get_position() {
        return {
            "x": this.x,
            "y": this.y
        };
    }

    get_id() {
        return this.id;
    }

    update_metadata(new_metadata) {
        for (let key in new_metadata) {
            this.metadata[key] = new_metadata[key];
        }
    }

    update_position(x, y) {
        this.x = x;
        this.y = y;
    }

    get_metadata() {
        return this.metadata;
    }

    get_name() {
        return this.metadata.name;
    }

    get_description() {
        return this.metadata.description;
    }

    get_page_ids() {
        let page_ids = [];
        for (let page_id in this.pages) {
            page_ids.push(page_id);
        }
        return page_ids;
    }

    get_url(page_id) {
        if (this.pages && this.pages[page_id].url) {
            return this.node_type.get_package_type().localise_url(this.pages[page_id].url);
        }
        return null;
    }

    get_page(page_id) {
        return this.pages[page_id] || {};
    }

    notify_connections_changed(connection_counts) {
        if (this.wrapper) {
            this.wrapper.notify_connections_changed(connection_counts);
        }
    }

    remove() {
        if (this.wrapper) {
            this.wrapper.remove();
        }
        this.removed = true;
    }

    serialise() {
        let obj = {
            "x": this.x,
            "y": this.y,
            "node_type": this.node_type.get_id(),
            "metadata": this.metadata
        };
        return obj;
    }

    get_type() {
        return this.node_type;
    }

    close() {
        this.wrapper.close();
    }
}

skadi.CoreNode.deserialise = (core, id, obj) => {
    let node_type = core.get_schema().get_node_type(obj.node_type);
    let node = new skadi.CoreNode(core, node_type, id, obj.x, obj.y, obj.metadata);
    return node;
};


