var skadi = skadi || {};

skadi.CoreLink = class {

  constructor(core, id, from_node, from_port_name, link_type, to_node, to_port_name) {
    this.core = core;
    this.id = id;

    this.from_node = from_node;
    this.to_node = to_node;

    this.from_port_name = from_port_name;
    this.to_port_name = to_port_name;

    this.link_type = link_type;

    this.removed = false;

  }

  get_id() {
    return this.id;
  }

  get_link_type() {
    return this.link_type;
  }

  get_from_node() {
    return this.from_node;
  }

  get_from_port_name() {
    return this.from_port_name;
  }

  get_to_node() {
    return this.to_node;
  }

  get_to_port_name() {
    return this.to_port_name;
  }

  remove() {
      this.removed = true;
  }

  serialise() {
    return {
      "to_port":this.to_node.get_id()+":"+this.to_port_name,
      "from_port":this.from_node.get_id()+":"+this.from_port_name,
      "link_type":this.link_type.get_id()
    };
  }
}

skadi.CoreLink.deserialise = function(core, id,obj, node_renamings) {

    let from_port = core.get_network().extract_address(obj.from_port);
    let to_port = core.get_network().extract_address(obj.to_port);
    let from_node_id = from_port.node;
    if (from_node_id in node_renamings) {
       from_node_id = node_renamings[from_node_id];
    }
    let from_node = core.get_network().get_node(from_node_id);

    let to_node_id = to_port.node;
    if (to_node_id in node_renamings) {
       to_node_id = node_renamings[to_node_id];
    }
    let to_node = core.get_network().get_node(to_node_id);

    let linkType = core.get_schema().get_link_type(obj.link_type);
    return new skadi.CoreLink(core, id, from_node, from_port.port, linkType, to_node, to_port.port);
}







