var skadi = skadi || {};

skadi.AboutDialogue = class {

    constructor(design, elt) {
        let rowdata_platform = [
            [this.tn("Name"), this.tn("Version"), this.tn("License Name"), this.tn("Link")],
            [this.tn("Skadi"), this.tn(skadi.VERSION), this.tn("MIT"), this.mklink("Github", "https://github.com/visual-topology/skadi")]
        ];

        try {
            let cls = zip.ZipReader;
            rowdata_platform.push([this.tn("zip.js"), this.tn("v2.7.52"), this.tn("BSD-3-Clause"), this.mklink("Github", "https://github.com/gildas-lormeau/zip.js")])
        } catch(ex) {
            /* zip.js is not installed */
        }

        design.core.platform_extensions.forEach(o => {
            rowdata_platform.push([this.tn(o["name"]), this.tn(o["version"]), this.tn(o["license_name"]), o["url"] ? this.mklink("About...", o["url"]) : this.tn("")]);
        });

        let rowdata_packages = [[this.tn("Name"), this.tn("Version"), this.tn("License Name"), this.tn("Link")]];
        let package_types = design.get_schema().get_package_types();
        package_types.forEach(package_type_id => {
            let pt = design.get_schema().get_package_type(package_type_id);
            let metadata = pt.get_metadata();
            rowdata_packages.push([this.tn(metadata.name),
                    this.tn(metadata.version),
                    this.tn(metadata.license_name),
                    this.mklink("About", pt.get_resource_url(metadata.link))
            ]);
        });
        elt.appendChild(document.createElement("h2").appendChild(this.tn("Platform")));
        elt.appendChild(this.tableize(rowdata_platform, "exo-dark-purple-bg exo-white-fg"));
        elt.appendChild(document.createElement("h2").appendChild(this.tn("Packages")));
        elt.appendChild(this.tableize(rowdata_packages, "exo-dark-orange-bg exo-white-fg"));
    }

    tableize(rowdata, header_row_class) {
        let tbl = document.createElement("table");
        tbl.setAttribute("class", "exo-full-width exo-border");
        rowdata.forEach(rowitem => {
            let row = document.createElement("tr");
            if (rowitem === rowdata[0] && header_row_class) {
                row.setAttribute("class", header_row_class);
            }
            rowitem.forEach(item => {
                let cell = document.createElement("td");
                cell.appendChild(item);
                row.appendChild(cell);
            });
            tbl.appendChild(row);
        });
        return tbl;
    }

    tn(txt) {
        return document.createTextNode(txt || "");
    }

    mklink(text, url) {
        let a = document.createElement("a");
        a.setAttribute("href", url);
        a.setAttribute("target", "_new");
        a.appendChild(document.createTextNode(text));
        return a;
    }

    close() {
    }
}

