var skadi = skadi || {};

skadi.ConfigurationDialogue = class {

    static html() {
        return `<h3>{{configuration.system.heading}}</h3>`;
    }

    constructor(design, elt) {
        this.design = design;
        this.status_divs = {};

        let header_div = document.createElement("div");
        header_div.innerHTML = design.localise(skadi.ConfigurationDialogue.html());
        elt.appendChild(header_div);

        /* language choice */
        {
            let select = design.core.get_l10n_utils().create_language_select();
            let row = document.createElement("div");
            row.setAttribute("class", "exo-row");

            let lang_cell = document.createElement("div");
            lang_cell.setAttribute("class", "exo-2-cell");
            lang_cell.appendChild(document.createTextNode(design.localise("{{configuration.system.language}}")));
            row.appendChild(lang_cell);

            let l10n_cell = document.createElement("div");
            l10n_cell.setAttribute("class", "exo-2-cell");
            l10n_cell.appendChild(select);
            row.appendChild(l10n_cell);

            elt.appendChild(row);
        }

        /* checkbox - add page on canvas */

        {
            let cb = document.createElement("exo-checkbox");
            cb.setAttribute("value",
                design.core.get_user_setting("open_page_on_canvas") ? "true" : "false");

            cb.addEventListener("change", (evt) => {
                design.core.set_user_setting("open_page_on_canvas", evt.target.checked);
            });
            let row = document.createElement("div");

            let desc_cell = document.createElement("div");
            desc_cell.setAttribute("class", "exo-2-cell");
            desc_cell.appendChild(document.createTextNode(
                design.localise("{{configuration.system.option.pages_on_canvas}}")));
            row.appendChild(desc_cell);

            row.setAttribute("class", "exo-row");

            let cb_cell = document.createElement("div");
            cb_cell.setAttribute("class", "exo-2-cell");
            cb_cell.appendChild(cb);
            row.appendChild(cb_cell);
            elt.appendChild(row);
        }

        elt.appendChild(document.createElement("hr"));

        let h = document.createElement("h3");
        h.appendChild(document.createTextNode(design.localise("{{configuration.package.heading}}")));
        elt.appendChild(h);

        for (let package_id in design.core.network.configurations) {
            let configuration = design.core.network.configurations[package_id];
            let package_type = configuration.get_package_type();
            let l10n_utils = package_type.get_l10n_utils();

            if (!l10n_utils && configuration.get_page_ids().length === 0) {
                continue;
            }

            {
                let name = package_type.get_metadata().name;
                let row = document.createElement("div");
                row.setAttribute("class", "exo-row");

                let label_cell = document.createElement("div");
                label_cell.setAttribute("class", "exo-2-cell");
                label_cell.appendChild(document.createTextNode(design.localise("{{configuration.package.name}}")));
                row.appendChild(label_cell);

                let name_cell = document.createElement("div");
                name_cell.setAttribute("class", "exo-2-cell");
                name_cell.appendChild(document.createTextNode(name));
                row.appendChild(name_cell);
                elt.appendChild(row);
            }

            if (l10n_utils) {

                let select = l10n_utils.create_language_select();
                let row = document.createElement("div");
                row.setAttribute("class", "exo-row");

                let lang_cell = document.createElement("div");
                lang_cell.setAttribute("class", "exo-2-cell");
                lang_cell.appendChild(document.createTextNode("Language"));
                row.appendChild(lang_cell);

                let l10n_cell = document.createElement("div");
                l10n_cell.setAttribute("class", "exo-2-cell");
                l10n_cell.appendChild(select);
                row.appendChild(l10n_cell);

                elt.appendChild(row);
            }

            configuration.get_page_ids().forEach(page_id => {
                let url = configuration.get_url(page_id);
                let page = configuration.get_page(page_id);
                if (url) {
                    let row = document.createElement("div");
                    row.setAttribute("class", "exo-row");

                    let cfg_cell = document.createElement("div");
                    cfg_cell.setAttribute("class", "exo-2-cell");
                    cfg_cell.appendChild(document.createTextNode(page.title));
                    row.appendChild(cfg_cell);

                    let btn = document.createElement("input");
                    btn.setAttribute("type", "button");
                    btn.setAttribute("value", page.button_label || "Open...");
                    btn.addEventListener("click", this.open_configuration_callback(design, package_id, page_id));

                    let btn_cell = document.createElement("div");
                    btn_cell.setAttribute("class", "exo-2-cell");
                    btn_cell.appendChild(btn);
                    row.appendChild(btn_cell);
                    elt.appendChild(row);
                }
            });


            let status_row = document.createElement("div");
            status_row.setAttribute("class", "exo-row skadi_status");

            let st_cell = document.createElement("div");
            st_cell.setAttribute("class", "exo-2-cell");
            st_cell.appendChild(document.createTextNode("Configuration Status"));
            status_row.appendChild(st_cell);

            let status_cell = document.createElement("div");
            status_cell.setAttribute("class", "exo-2-cell");
            status_cell.setAttribute("style", "display:inline-block;");

            status_row.appendChild(status_cell);

            this.status_divs[package_id] = status_cell;

            elt.appendChild(status_row);


        }
        this.status_event_handler = this.design.core.add_configuration_status_event_handler((package_id, state, message) => {
                this.update_configuration_status(package_id, state, message);
        });
    }

    update_configuration_status(package_id, status_state, message) {
        if (package_id in this.status_divs) {
            let status_div = this.status_divs[package_id];
            status_div.innerHTML = "";
            let status_icon = skadi.create_icon_for_status_state(status_state);
            if (status_icon) {
                status_div.appendChild(status_icon);
            }
            if (message) {
                let text = document.createElement("span");
                text.appendChild(document.createTextNode(message));
                text.setAttribute("style","vertical-align:middle;");
                status_div.appendChild(text);
            }
        }
    }

    open_configuration_callback(design, package_id, page_id) {
        return (ev) => {
            design.open_configuration(package_id, page_id);
        }
    }

    close() {
        this.design.core.remove_configuration_status_event_handler(this.status_event_handler);
    }
}





/* src/js/controls/button.js */

var skadi = skadi || {};

skadi.Button = class {

  constructor(design, x,y,width,height,icon_url,onclick,tooltip_text) {
    this.design = design;
    this.x = x;
    this.y = y;
    this.width = width;
    this.height = height;
    this.icon_url = icon_url;
    this.button = null;
    this.onclick = onclick;
    this.fill = "";
    this.enabled = true;
    this.tooltip_text = tooltip_text;
    this.tooltip = null;
  }

  set_fill(fill) {
      this.fill = fill;
  }

  set_url(url) {
    this.icon_url = url;
    if (this.button) {
        this.button.attr("href",this.icon_url);    
    }
  }

  enable() {
    this.enabled = true;
    this.set_opacity(1.0);
  }

  disable() {
    this.enabled = false;
    this.set_opacity(0.5);
  }

  set_visible(visible) {
      let visibility = "visible";
      if (!visible) {
          visibility = "hidden";
      }
    
    if (this.button) {
        this.button.attr("visibility",visibility);    
    }
    if (this.rect) {
        this.rect.attr("visibility",visibility);    
    }
  }

  set_opacity(opacity) {
    if (this.button) {
        this.button.attr("opacity",opacity);    
    }
    if (this.rect) {
        this.rect.attr("opacity",opacity);    
    }
}

  draw(grp) {
  
    if (this.fill) {
        this.rect = grp.append("rect")
            .attr("width",this.width)
            .attr("height",this.height)
            .attr("x",this.x-this.width/2)
            .attr("y",this.y-this.height/2)
            .attr("fill",this.fill);  
    }

    this.button = grp.append("image")
      .attr("width",this.width)
      .attr("height",this.height)
      .attr("x",this.x-this.width/2)
      .attr("y",this.y-this.height/2)
      .attr("href",this.icon_url);
    let that = this;
    let onclick = function(ev) {
      if (that.enabled && that.onclick) {
        that.onclick(ev);
      }
    }
    this.button.on("click", onclick);
    if (this.tooltip_text) {
        this.set_tooltip(this.tooltip_text);
    }
  }

  get_size() {
    return { "width":this.width, "height":this.height };
  }

  get_position() {
    return { "x":this.x, "y":this.y };
  }

  set_tooltip(text) {
    this.tooltip_text = text;
    if (this.tooltip) {
      this.tooltip.update_text(text);
    } else {
      this.tooltip = new skadi.Tooltip(this.button.node(),this.design.get_svg_tooltip_group().node(),text);
    }
  }

  update_position(x,y) {
      this.x = x;
      this.y = y;
      if (this.button) {
        this.button
            .attr("x",this.x-this.width/2)
            .attr("y",this.y-this.height/2);
      }
      if (this.rect) {
        this.rect
            .attr("x",this.x-this.width/2)
            .attr("y",this.y-this.height/2);
      }
  }
}


