        this.drag
            .on("start", (x, y) => {
                this.drag.start_dragging = true;
            })
            .on("drag", (x, y) => {
                if (this.drag.start_dragging) {
                    this.drag.start_x = x;
                    this.drag.start_y = y;
                    this.drag.start_dragging = false;
                } else {
                    let dx = x - this.drag.start_x;
                    let dy = y - this.drag.start_y;
                    this.drag.start_x = x;
                    this.drag.start_y = y;
                    this.panzoom(dx, dy, 1);
                }
            })
