        this.configuration_status_handler = this.core.add_configuration_status_event_handler((package_id,state,message) => {
            let configuration = this.configurations[package_id];
            // ????
        });

        this.node_execution_handler = this.core.add_node_execution_event_handler((node_id,execution_state) => {
            let node = this.nodes[node_id];
            if (node) {
                node.update_execution_state(execution_state);
            }
        });
