var skadi = skadi || {};

skadi.Link = class {

  constructor(design, core_link) {

    let from_node = design.get_node(core_link.from_node.get_id());
    this.from_port = from_node.get_ports()[core_link.from_port_name];
    // to_node will not exist if the link is being opened
    let to_node = core_link.to_node ? design.get_node(core_link.to_node.get_id()) : null;
    this.to_port = to_node ? to_node.get_ports()[core_link.to_port_name] : null;

    this.core_link = core_link;
    this.design = design;

    this.from_port.add_link(this, false);
    if (this.to_port) {
      this.to_port.add_link(this, true);
    }

    this.menu_dial = null;
  }

  get_id() {
    return this.core_link.id;
  }

  get_from_port() {
    return this.from_port;
  }

  get_to_port() {
    return this.to_port;
  }

  get_link_type() {
    return this.core_link.get_link_type();
  }

  get_from_node() {
    return this.core_link.get_from_node();
  }

  get_from_port_name() {
    return this.core_link.get_from_port_name();
  }

  get_to_node() {
    return this.core_link.get_to_node();
  }

  get_to_port_name() {
    return this.core_link.get_to_port_name();
  }

  remove() {
    this.from_port.remove_link(this);
    if (this.to_port) {
      this.to_port.remove_link(this);
    }
    skadi.x3.select("#" + this.get_id()).remove();
    this.tooltip.remove();
  }

  draw(endx, endy) {
    let container = this.design.get_node_group();

    this.grp = container.append("g").attr("id", this.get_id()).attr("class", "link");

    this.path = this.grp.append("path")
      .attr("id", this.get_id() + "path")
      .attr("class", "mainpath");

    this.path.attr("stroke", this.design.get_schema().get_link_colour(this.core_link.link_type.get_id()));

    let handler = (evt) => {
      if (this.menu_dial) {
        return;
      }
      let mitems = [];
      mitems.push(new skadi.TextMenuDialogue.MenuItem("{{link.menu.remove}}", () => {
        this.design.remove_link(this.get_id(), false);
      }));
      let evloc =  skadi.x3.get_event_xy(evt);
      let tm = new skadi.TextMenuDialogue(this.design, mitems, () => { this.menu_dial = null; }, this, evloc.x - 50, evloc.y - 50, "{{link.menu.title}}");
      tm.open();
    };

    // this.path.on("click touchstart", handler);
    this.path.on("click", handler);

    this.update_position(endx,endy);
    this.update_opacity();

    this.tooltip = new skadi.Tooltip(this.grp.node(),this.design.get_svg_tooltip_group().node(),this.core_link.link_type.get_name() || "");
  }

  set_opacity(opacity) {
    this.grp.attr("style", "opacity:" + opacity + ";");
  }

  update_opacity() {
    this.set_opacity(this.stopped ? 0.3 : 1.0);
  }

  update_position(endx,endy) {

    let coordsFrom = this.from_port.get_link_position();

    let d = "";
    if (this.to_port) {
      let coordsTo = this.to_port.get_link_position();
      let midx = coordsFrom.x2 + (coordsTo.x2 - coordsFrom.x2) / 2;
      let midy = coordsFrom.y2 + (coordsTo.y2 - coordsFrom.y2) / 2;
      d = "M" + coordsFrom.x + "," + coordsFrom.y + " L" + coordsFrom.x1 + "," + coordsFrom.y1 +
        " Q" + coordsFrom.x2 + "," + coordsFrom.y2 + " " + midx + "," + midy +
        " T" + coordsTo.x1 + "," + coordsTo.y1 + this.compute_arrow_path(coordsTo);
    } else {
      let midx = coordsFrom.x2 + (endx - coordsFrom.x2) / 2;
      let midy = coordsFrom.y2 + (endy - coordsFrom.y2) / 2;
      let coordsTo = {
        "x": endx,
        "y": endy,
        "x1": midx,
        "y1": midy
      };
      d = "M" + coordsFrom.x + "," + coordsFrom.y + " L" + coordsFrom.x1 + "," + coordsFrom.y1 +
        " Q" + coordsFrom.x2 + "," + coordsFrom.y2 + " " + midx + "," + midy +
        " L" + coordsTo.x + "," + coordsTo.y + " " + this.compute_arrow_path(coordsTo);
    }
    this.path.attr("d", d);
  }

  compute_arrow_path(coords) {
    let theta = Math.atan2(coords.y1 - coords.y, coords.x1 - coords.x);
    if (this.to_port) {
      theta = this.to_port.get_port_angle();
    }
    let theta1 = theta - Math.PI / 8;
    let theta2 = theta + Math.PI / 8;
    let len = 15;
    let x1 = coords.x + len * Math.cos(theta1);
    let y1 = coords.y + len * Math.sin(theta1);
    let x2 = coords.x + len * Math.cos(theta2);
    let y2 = coords.y + len * Math.sin(theta2);
    let path = " M" + x1 + "," + y1 + " L" + coords.x + "," + coords.y + " L" + x2 + "," + y2;
    return path;
  }
}




  



