var skadi = skadi || {};

skadi.Port = class {

  constructor(design, node, id, port_type, port_name, in_not_out) {
    this.design = design;
    this.node = node;
    this.id = id;
    this.type = "";
    this.port_name = port_name;
    this.port_type = port_type;
    this.in_not_out = in_not_out;
    this.out_links = {};
    this.in_links = {};
    this.link_type_id = port_type.link_type;
    this.link_type = this.design.get_schema().get_link_type(this.link_type_id);
    this.link_spacing = 20;
    if (this.shape) {
      this.shape.style("fill", this.design.get_link_colour(this.link_type_id));
    }

    this.r = 5;

    this.grp = null;
  }

  get_link_type() {
    return this.link_type_id;
  }

  get_id() {
    return this.id;
  }

  get_node() {
    return this.node;
  }

  get_port_name() {
    return this.port_name;
  }

  is_input() {
    return this.in_not_out;
  }

  remove() {
    skadi.x3.select("#" + this.id.replace(":", "_")).remove();
    this.tooltip.remove();
  }

  set_class(highlight) {
    if (this.grp) {
      let portclass = "port ";
      if (this.in_not_out) {
        portclass += "portin";
      } else {
        portclass += "portout";
      }
      if (highlight) {
        portclass += " highlight";
      }
      this.grp.attr("class", portclass);
    }
  }

  get_address() {
    return {
        "node": this.node.get_id(),
        "port": this.port_name
    };
  }

  draw(parent) {
    let container = parent ? parent : this.design.getCellGroup();
    let coords = this.node.get_port_position(this.port_name, this.in_not_out);
    this.r = coords.r;

    this.grp = container.append("g").attr("id", this.id.replace(":", "_"));
    this.set_class();

    this.shape = this.grp.append("circle")
      .attr("cx", coords.cx)
      .attr("cy", coords.cy)
      .attr("r", this.r)
      .attr("class", "port_inner")
      .style("fill", this.link_type.get_colour());

    let that = this;
    if (this.node.active && !this.in_not_out) {
      let drag =  skadi.x3.drag();
      drag
        .on("start_abs", function() {
          that.node.draggingFrom = true;
        })
        .on("drag_abs", function(x,y) {
          if (that.node.draggingFrom) {
            that.dragx = x - that.design.offset_x;
            that.dragy = y - that.design.offset_y;
            let cc = that.design.to_canvas_coords(that.dragx,that.dragy);
            let dest = that.design.find_input_port(cc.x, cc.y, that.link_type_id);
            if (dest) {
              if (drag.highlighted_port && drag.highlighted_port != dest) {
                drag.highlighted_port.set_class(false);
              }
              drag.highlighted_port = dest;
              dest.set_class(true);
            } else {
              if (drag.highlighted_port) {
                drag.highlighted_port.set_class(false);
                drag.highlighted_port = null;
              }
            }
            if (that.conn == null) {
              that.conn = {};
              let id = that.design.next_id("link");
              let core_link = new skadi.CoreLink(that.design.core, id, that.get_node(), that.get_port_name(),that.link_type, null, null);
              that.conn = new skadi.Link(that.design, core_link, that, null);
              that.conn.draw(cc.x, cc.y);
            } else {
              if (that.conn.update_position) {
                that.conn.update_position(cc.x, cc.y);
              }
            }
          }
        })
        .on("end_abs", function() {
          if (drag.highlighted_port) {
            drag.highlighted_port.set_class(false);
            drag.highlighted_port = null;
          }
          if (that.conn) {
            let cc = that.design.to_canvas_coords(that.dragx,that.dragy);
            let dest = that.design.find_input_port(cc.x, cc.y, that.link_type_id);
            let from_node_id = that.get_node().get_id();
            let to_node_id = dest ? dest.get_node().get_id() : undefined;
            if (dest && dest.is_input() && !that.design.already_connected(that,dest) && from_node_id != to_node_id
                && that.design.check_allowed_to_connect(from_node_id, to_node_id)) {
              if (!dest.port_type.get_allow_multiple_connections()) {
                dest.remove_input_links();
              }
              if (!that.port_type.get_allow_multiple_connections()) {
                that.remove_output_links();
              }

              that.conn.remove();
              that.design.create_link(that, dest, that.link_type_id);
            } else {
              that.conn.remove();
            }
            that.conn = null;
          }
          that.node.draggingFrom = false;
        });

      this.shape.call(drag);
    } else {
      let drag =  skadi.x3.drag();
      this.shape.call(drag);
    }
    let tooltip_text = (this.in_not_out ? "[in] " : "[out] ") + this.label;
    this.tooltip = new skadi.Tooltip(this.grp.node(),this.design.get_svg_tooltip_group().node(),tooltip_text);
  }

  update_position() {
    let coords = this.node.get_port_position(this.port_name, this.in_not_out);
    this.r = coords.r;
    this.shape.attr("cx", coords.cx).attr("cy", coords.cy).attr("r", this.r);

    for (let k in this.out_links) {
      this.out_links[k].update_position();
    }
    for (let k in this.in_links) {
      this.in_links[k].update_position();
    }
  }

  get_position() {
    let coords = this.node.get_port_position(this.port_name, this.in_not_out);
    return {
      "x": coords.cx,
      "y": coords.cy
    };
  }

  get_port_angle() {
    return this.node.get_port_position(this.port_name,this.in_not_out).theta;
  }

  get_link_position() {
    return this.node.get_port_link_position(this.port_name, this.in_not_out);
  }

  add_link(link, in_not_out) {
    if (in_not_out) {
      this.in_links[link.get_id()] = link;
    } else {
      this.out_links[link.get_id()] = link;
    }
  }

  remove_link(link) {
    if (this.in_not_out) {
      delete this.in_links[link.get_id()];
    } else {
      delete this.out_links[link.get_id()];
    }
  }

  get_link_count() {
    let count = 0;
    if (this.in_not_out) {
      for (let key in this.in_links) {
        count += 1;
      }
    } else {
      for (let key in this.out_links) {
        count += 1;
      }
    }
    return count;
  }

  remove_input_links() {
    if (this.in_not_out) {
      let keys = [];
      for (let key in this.in_links) {
        keys.push(key);
      }
      for (let idx in keys) {
        let key = keys[idx];
        this.design.remove(this.in_links[key].get_id());
      }
    }
  }

  remove_output_links() {
    if (!this.in_not_out) {
      let keys = [];
      for (let key in this.out_links) {
        keys.push(key);
      }
      for (let idx in keys) {
        let key = keys[idx];
        this.design.remove(this.out_links[key].get_id());
      }
    }
  }
}



