skadi.Api = class {

    constructor() {
        this.core = null;
    }

    set_core(core) {
        this.core = core;
        window.addEventListener("beforeunload", (evt) => {
            // this is triggered only if the user has had some interaction with the page
            this.core.close();
            // evt.preventDefault();
        });
    }

    get_core() {
        return this.core;
    }

    has_node(node_id) {
        return this.core.get_network().has_node(node_id);
    }

    register_platform_extension(name, version, url) {
        this.extensions.push({
            "name": name,
            "version": version,
            "url": url
        });
    }

    async load_l10n(l10n_base_url) {
        let l10n_utils = new skadi.L10NUtils("system", l10n_base_url || skadi.api_home_url + "/l10n");
        await l10n_utils.initialise();
        return l10n_utils;
    }

    /**
     * Load schemas into skadi
     *
     * @param {Array} package_urls - an array of urls pointing to schema files
     * @param {Object} resource_loader - optional, a ResourceLoader class instance
     */
    async load_schema(package_urls, resource_loader) {
        async function load_package_from_url(url,schema) {
            return await fetch(url)
                .then(response => response.json())
                .then(schema_config => schema.load_package(url, schema_config, resource_loader));
        }

        let schema = new skadi.Schema();

        for (let idx = 0; idx < package_urls.length; idx++) {
            await load_package_from_url(package_urls[idx],schema);
        }
        return schema;
    }

    async load(from_obj, node_renamings, suppress_callbacks) {
        return await this.core.deserialise(from_obj ,node_renamings, suppress_callbacks);
    }

    save() {
        return this.core.serialise();
    }

    get_package_list() {
        return this.core.schema.get_package_types();
    }

    get_network() {
        return this.core.get_network();
    }

    set_node_status(node_id, status_msg, state) {
        this.core.update_node_status(node_id, state, status_msg);
    }

    set_configuration_status(package_id, status_msg, state) {
        this.core.update_configuration_status(package_id, state, status_msg);
    }

    update_execution_state(node_id, state, is_manual) {
        this.core.update_execution_state(node_id, state, is_manual);
    }

    execution_complete() {
        this.core.execution_complete();
    }

    get_version() {
        return skadi.version;
    }

    add_node_event_handler(node_event_type, handler) {
        switch(node_event_type) {
            case "add":
                this.core.add_node_event_handler(node_event_type,handler);
                break;
            case "remove":
                this.core.add_node_event_handler(node_event_type,handler);
                break;
            case "update_position":
                this.core.add_node_event_handler(node_event_type,handler);
                break;
            case "update_metadata":
                this.core.add_node_event_handler(node_event_type,handler);
                break;
            default:
                console.warn("Unhandled event type: "+node_event_type);

        }
        return handler;
    }

    remove_node_event_handler(node_event_type, handler) {
        this.core.remove_node_event_handler(node_event_type, handler);
    }

    add_link_event_handler(link_event_type, handler) {
        this.core.add_link_event_handler(link_event_type,handler);
        return handler;
    }

    remove_link_event_handler(link_event_type, handler) {
        this.core.remove_node_event_handler(link_event_type, handler);
    }

    add_design_event_handler(design_event_type, handler) {
        this.core.add_design_event_handler(design_event_type, handler);
        return handler;
    }

    remove_design_event_handler(design_event_type, handler) {
        this.core.remove_design_event_handler(design_event_type, handler);
    }
}

