/* src/js/common/x3.js */

var skadi = skadi || {};

skadi.X3Drag = class {

    constructor() {
        this.callbacks = {};
        this.elt = null;
        this.drag_overlay = document.getElementById("drag_overlay");
        this.start_time = null;
        this.start_pos = null;
    }

    set_element(elt) {
        this.elt = elt;
        let that = this;
        this.elt.onmousedown = function (evt) {
            that.start(evt);
        }
    }

    on(event, callback) {
        this.callbacks[event] = callback;
        return this;
    }

    start(evt) {
        let that = this;
        this.start_time = Date.now();
        evt.stopPropagation();

        document.onmousemove = function (evt) {
            that.initial_move(evt);
        }

        document.onmouseup = function (evt) {
            that.initial_mouseup(evt);
        }

        let pos = skadi.x3.get_event_xy(evt);
        this.start_pos = pos;
    }

    initial_move(evt) {
        document.onmousemove = null;
        document.onmouseup = null;
        this.enable_drag_overlay();
    }

    initial_mouseup(evt) {
        document.onmousemove = null;
        document.onmouseup = null;
    }

    enable_drag_overlay() {
        let that = this;
        this.drag_overlay.style = "pointer-events:all;";
        this.drag_overlay.setAttribute("width", "100%");
        this.drag_overlay.setAttribute("height", "100%");
        this.drag_overlay.setAttribute("class", "drag_overlay_open");

        this.drag_overlay.onmousemove = function (evt) {
            that.move(evt);
        }
        this.drag_overlay.onmouseup = function (evt) {
            that.end(evt);
        }
        this.drag_overlay_enabled = true;
    }

    start_cb() {
        if ("start" in this.callbacks) {
            this.callbacks["start"](this.start_pos.x, this.start_pos.y);
        }
        if ("start_abs" in this.callbacks) {
            this.callbacks["start_abs"](this.start_pos.ax, this.start_pos.ay);
        }
    }

    move(evt) {
        let elapsed_ms = Date.now() - this.start_time;
        if (elapsed_ms < 200) {
            return;
        }
        if (this.start_pos) {
            this.start_cb();
            this.start_pos = null;
        }
        evt.preventDefault();
        evt.stopPropagation();
        let pos = skadi.x3.get_event_xy(evt);
        if ("drag" in this.callbacks) {
            this.callbacks["drag"](pos.x, pos.y);
        }
        if ("drag_abs" in this.callbacks) {
            this.callbacks["drag_abs"](pos.ax, pos.ay);
        }
    }

    end(evt) {
        let elapsed_ms = Date.now() - this.start_time;
        if (elapsed_ms < 200) {
            // ignore drag end, this event should be followed by a click
        } else {
            if (this.start_pos) {
                this.start_cb();
                this.start_pos = null;
            }
            let pos = skadi.x3.get_event_xy(evt);
            evt.preventDefault();
            evt.stopPropagation();
            if ("end" in this.callbacks) {
                this.callbacks["end"](pos.x, pos.y);
            }
            if ("end_abs" in this.callbacks) {
                this.callbacks["end_abs"](pos.ax, pos.ay);
            }
        }
        this.drag_overlay.style = "pointer-events:none;";
        this.drag_overlay.setAttribute("width", 0);
        this.drag_overlay.setAttribute("height", 0);
        this.drag_overlay.setAttribute("class", "drag_overlay");
        this.drag_overlay.onmousemove = null;
        this.drag_overlay.onmouseup = null;
    }
}

skadi.X3Selection = class {

    constructor(elts) {
        this.svg_xmlns = "http://www.w3.org/2000/svg";
        this.xlink_xmlns = "http://www.w3.org/1999/xlink";
        this.html_xmlns = "http://www.w3.org/1999/xhtml";
        this.elts = [];

        for (let idx = 0; idx < elts.length; idx++) {
            this.elts.push(elts[idx]);
        }
    }

    add_node(node) {
        this.elts.push(node);
    }

    remove() {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].remove();
        }
        this.elts = [];
        return this;
    }

    is_svg(tag,parent_elt) {
        if (tag === "svg") {
            return true;
        }
        return parent_elt.namespaceURI === this.svg_xmlns;
    }

    append(tag, is_svg) {
        let newSelection = new skadi.X3Selection([]);
        for (let idx = 0; idx < this.elts.length; idx++) {
            let use_svg = is_svg;
            if (use_svg === undefined) {
                use_svg = this.is_svg(tag,this.elts[idx]);
            }
            let n;
            if (use_svg) {
                n = document.createElementNS(this.svg_xmlns, tag);
            } else {
                if (tag == "href") {
                    n = document.createElementNS(this.xlink_xmlns, tag);
                } else {
                    n = document.createElementNS(this.html_xmlns, tag);
                }
            }
            this.elts[idx].appendChild(n);
            newSelection.add_node(n);
        }
        return newSelection;
    }

    insert(tag, is_svg) {
        let newSelection = new skadi.X3Selection([]);
        for (let idx = 0; idx < this.elts.length; idx++) {
            let use_svg = is_svg;
            if (use_svg === undefined) {
                use_svg = this.is_svg(tag,this.elts[idx]);
            }
            let n;
            if (use_svg) {
                n = document.createElementNS(this.svg_xmlns, tag);
            } else {
                if (tag == "href") {
                    n = document.createElementNS(this.xlink_xmlns, tag);
                } else {
                    n = document.createElementNS(this.html_xmlns, tag);
                }
            }
            this.elts[idx].insertBefore(n, this.elts[idx].firstChild);
            newSelection.add_node(n);
        }
        return newSelection;
    }

    attr(name, value) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].setAttribute(name, value);
        }
        return this;
    }

    attr_ns(namespace, name, value) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].setAttributeNS(namespace, name, value);
        }
        return this;
    }

    text(txt) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            // this.elts[idx].innerHTML = "";
            let tn = document.createTextNode(txt);
            this.elts[idx].appendChild(tn);
        }
        return this;
    }

    add_class(cls) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].classList.add(cls);
        }
        return this;
    }

    remove_class(cls) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].classList.remove(cls);
        }
        return this;
    }

    html(inner_html) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].innerHTML = inner_html;
        }
        return this;
    }

    node() {
        if (this.elts) {
            return this.elts[0];
        } else {
            return null;
        }
    }

    style(name, value) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].style[name] = value;
        }
        return this;
    }

    call(drag) {
        if (this.elts.length === 1) {
            drag.set_element(this.elts[0]);
        } else {
            console.log("Unable to set up drag handler, not exactly 1 element in selection");
        }
    }

    on(event, callback) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].addEventListener(event, callback);
        }
        return this;
    }

    ignore_mouse_events() {
        ["mousemove"].forEach(event_type =>
            this.on(event_type, evt => {
                evt.stopPropagation();
                evt.preventDefault();
            }));
    }
}

skadi.X3 = class {

    constructor() {
    }

    select(selector) {
        let elts = document.querySelectorAll(selector);
        return new skadi.X3Selection(elts);
    }

    get_node_pos(elt) {
        let body = this.select('body').node();

        let x = 0;
        let y = 0;

        while (elt != null && elt != body) {
            x += (elt.offsetLeft || elt.clientLeft);
            y += (elt.offsetTop || elt.clientTop);
            elt = (elt.offsetParent || elt.parentNode);
        }

        return {"x": x, "y": y};
    }

    get_event_xy(e) {
        let pos = {};
        pos.x = e.clientX;
        pos.y = e.clientY;
        pos.ax = pos.x + window.scrollX;
        pos.ay = pos.y + window.scrollY;
        return pos;
    }

}

skadi.x3 = new skadi.X3();
skadi.x3.drag = function () {
    return new skadi.X3Drag();
}

skadi.$ = (id) => {
    return document.getElementById(id);
}

skadi.$$ = (selector) => {
    return skadi.x3.select(selector);
}

skadi.$$$ = (id) => {
    return new skadi.X3Selection([skadi.$(id)]);
}




/* src/js/utils/index_db.js */

var skadi = skadi || {};

skadi.IndexDB = class {

    constructor(name) {
        this.name = "topology-"+name;
    }

    async init() {
        this.db = await this.open();
    }

    async open() {
        return await new Promise((resolve,reject) => {
            const request = indexedDB.open(this.name, 1);
            request.onsuccess = (evt) => {
                resolve(evt.target.result);
            }
            request.onerror = (evt) => {
                console.error(evt.target.errorCode);
                resolve(null);
            }
            request.onupgradeneeded = (evt) => {
                // Save the IDBDatabase interface
                let db = evt.target.result;
                db.createObjectStore("data", {});
            }
        });
    }


    async get(key) {
        return await new Promise((resolve,reject) => {
            const transaction = this.db.transaction(["data"], "readonly");
            const request = transaction.objectStore("data").get(key);
            request.onsuccess = (evt) => {
                resolve(evt.target.result);
            }
            request.onerror = (evt) => {
                resolve(undefined);
            }
        });
    }

    async put(key, value) {
        return await new Promise((resolve,reject) => {
            const transaction = this.db.transaction(["data"], "readwrite");
            const request = transaction.objectStore("data").put(value,key);
            request.onsuccess = (evt) => {
                resolve(true);
            }
            request.onerror = (evt) => {
                resolve(true);
            }
        });
    }

    async delete(key) {
        return await new Promise((resolve,reject) => {
            const transaction = this.db.transaction(["data"], "readwrite");
            const request = transaction.objectStore("data").delete(key);
            request.onsuccess = (evt) => {
                resolve(evt.target.result);
            }
            request.onerror = (evt) => {
                resolve(undefined);
            }
        });
    }

    async getAllKeys() {
        return await new Promise((resolve,reject) => {
            const transaction = this.db.transaction(["data"], "readonly");
            const request = transaction.objectStore("data").getAllKeys();
            request.onsuccess = (evt) => {
                resolve(evt.target.result);
            }
            request.onerror = (evt) => {
                resolve(undefined);
            }
        });
    }
}

skadi.IndexDB.create = async function(name) {
    let db = new skadi.IndexDB(name);
    await db.init();
    return db;
}

skadi.IndexDB.remove = async function(name) {
    return await new Promise((resolve,reject) => {
        const request = indexedDB.deleteDatabase(name);
        request.onsuccess = (evt) => {
            resolve(true);
        }
        request.onerror = (evt) => {
            console.error(evt.target.errorCode);
            resolve(false);
        }
    });
}

/* src/js/utils/client_storage.js */

skadi = skadi || {};

skadi.ClientStorage = class {

    constructor(db_name) {
        this.db_name = db_name;
        this.db = null;
    }

    static check_valid_key(key) {
        if (!key.match(/^[0-9a-zA-Z_]+$/)) {
            throw new Error("data key can only contain alphanumeric characters and underscores");
        }
    }

    static check_valid_value(data) {
        if (data instanceof ArrayBuffer) {
            return;
        } else if (data === null) {
            return;
        }
        throw new Error("data value can only be null or ArrayBuffer")
    }

    async open() {
        this.db = await skadi.IndexDB.create(this.db_name);
    }

    close() {
        this.db = null;
    }

    async get_item(key) {
        if (!this.db) {
            await this.open();
        }
        let result = await this.db.get(key);
        if (result === undefined) {
            result = null;
        }
        return result;
    }

    async set_item(key, value) {
        if (!this.db) {
            await this.open();
        }
        await this.db.put(key, value);
    }

    async remove_item(key) {
        if (!this.db) {
            await this.open();
        }
        await this.db.delete(key);
    }

    async remove() {
        this.close();
        await skadi.IndexDB.remove(this.db_name);
    }

    async get_keys() {
        if (!this.db) {
            await this.open();
        }
        return await this.db.getAllKeys();
    }

    async clear() {
        // clear the database by removing and re-opening
        await this.remove();
        await this.open();
    }
}

/* src/js/utils/l10n_bundle.js */

var skadi = skadi || {};

skadi.L10NBundle = class {

    constructor(bundle_content) {
        this.bundle_content = bundle_content;
    }

    localise(input) {
        if (input in this.bundle_content) {
            return this.bundle_content[input];
        }
        // for empty bundles, localise returns the input
        if (Object.keys(this.bundle_content).length == 0) {
            return input;
        }
        // treat the input as possibly containing embedded keys, delimited by {{ and }},
        // for example "say {{hello}}" embeds they key hello
        // substitute any embedded keys and the surrounding delimiters with their values, if the key is present in the bundle
        let idx = 0;
        let s = "";
        while(idx<input.length) {
            if (input.slice(idx, idx+2) === "{{") {
                let startidx = idx+2;
                idx += 2;
                while(idx<input.length) {
                    if (input.slice(idx,idx+2) === "}}") {
                        let token = input.slice(startidx,idx);
                        if (token in this.bundle_content) {
                            token = this.bundle_content[token];
                        }
                        s += token;
                        idx += 2;
                        break;
                    } else {
                        idx += 1;
                    }
                }
            } else {
                s += input.charAt(idx);
                idx++;
            }
        }
        return s;
    }

    get_content() {
        return this.bundle_content;
    }
}



/* src/js/utils/l10n_utils.js */

var skadi = skadi || {};

skadi.L10NUtils = class {

    constructor(id, l10n_folder_url) {
        this.id = id;
        this.l10n_folder_url = l10n_folder_url;
        this.metadata = null;
        this.bundles = {};
        this.language = "";
        this.language_update_listeners = [];
    }

    configure_for_package(package_l10n) {
        this.metadata = package_l10n;
    }

    async initialise() {
        if (this.metadata === null) {
            this.metadata = await fetch(this.l10n_folder_url+"/index.json").then(r => r.json(), e => null);
        }
        for(let language in this.metadata.languages) {
            let bundle_url = this.l10n_folder_url+"/"+this.metadata.languages[language].bundle_url;
            try {
                let r = await fetch(bundle_url);
                let o = await r.json();
                this.bundles[language] = new skadi.L10NBundle(o);
            } catch(ex) {
                console.error("l10utils: "+this.id+" failed to load bundle: "+bundle_url);
            }
        }
        let language = window.localStorage.getItem("/skadi/settings/l10n."+this.id+".language") || this.metadata.default_language;
        await this.set_language(language);
        console.log("l10utils: "+this.id+" loaded bundles:"+Object.keys(this.bundles));
    }

    set_language(language) {
        window.localStorage.setItem("/skadi/settings/l10n."+this.id+".language", language);
        this.language = language;
        this.language_update_listeners.forEach((callback) => callback(language));
    }

    get_bundles() {
        return this.bundles;
    }

    get_language() {
        return this.language;
    }

    get_languages() {
        let key_name_list = [];
        for(let language_key in this.metadata.languages) {
            key_name_list.push([language_key,this.metadata.languages[language_key].name]);
        }
        return key_name_list;
    }

    localise(input,for_language) {
        let language = for_language || this.language;
        if (language in this.bundles) {
            return this.bundles[language].localise(input);
        }
        return input;
    }

    add_language_update_listener(listener) {
        this.language_update_listeners.push(listener);
        return listener;
    }

    remove_language_update_listener(listener) {
        this.language_update_listeners.filter(item => item != listener)
    }

    create_language_select() {
        let select = document.createElement("select");
        let languages = this.get_languages();
        for(var idx=0; idx<languages.length; idx++) {
            let option = document.createElement("option");
            option.setAttribute("value",languages[idx][0]);
            option.appendChild(document.createTextNode(languages[idx][1]));
            select.appendChild(option);
        }
        select.value = this.get_language();
        select.addEventListener("change", (ev) => {
            this.set_language(select.value);
            location.reload();
        });
        return select; 
    }
}



/* src/js/utils/resource_loader.js */

var skadi = skadi || {};

skadi.ResourceLoader = class {

    constructor(js_parent, css_parent) {
        this.loading_resources = {}; // resource_name -> true
        this.loaded_resources = {}; // resource name -> true|false
        this.js_parent = js_parent || document.head;
        this.css_parent = css_parent || document.head;
    }

    async css_load(resource) {
        let that = this;
        await fetch(resource).then(r => r.text(), e => that.notify(resource,false)).then(txt => {
            let ele = document.createElement("style");
            ele.appendChild(document.createTextNode(txt));
            this.css_parent.appendChild(ele);
            that.notify(resource,true);
        });
    }

    async js_load(resource) {
        let script = document.createElement("script");
        script.onload = (evt) => this.notify(resource, true);
        script.setAttribute("src",resource);
        document.head.appendChild(script);
        while(true) {
            await new Promise(r => setTimeout(r, 100));
            if (resource in this.loaded_resources) {
                break;
            }
        }
    }

    async load(resource) {
        // start new load
        this.loading_resources[resource] = true;
        if (resource.endsWith(".css")) {
            await this.css_load(resource);
        } else  {
            await this.js_load(resource);
        }
    }

    notify(resource,success) {
        delete this.loading_resources[resource];
        this.loaded_resources[resource] = success;
    }

    async load_resources(resource_list) {
        let load_tasks = [];
        for(let idx=0; idx<resource_list.length; idx++) {
            let resource = resource_list[idx];
            if (resource in this.loading_resources || resource in this.loaded_resources) {
                /* resource already loaded or failed to load */
            } else {
                await this.load(resource);
            }
        }
        let results = {};
        for(let idx=0; idx<resource_list.length; idx++) {
            let resource = resource_list[idx];
            results[resource] = this.loaded_resources[resource];
        }
        return results;
    }
}



/* src/js/plugins/topology_store.js */

var skadi = skadi || {};

skadi.TopologyStore = class {

    constructor(workspace_id) {
        this.workspace_id = workspace_id;
        this.designer_or_application = null;
        this.db = null;
    }

    get_workspace_path(path) {
        return "workspace."+this.workspace_id+"."+path;
    }

    get_topology_path(path) {
        return "workspace."+this.workspace_id+".topology."+path
    }

    async init()  {
    }

    async open_workspace() {
        let db = new skadi.ClientStorage(this.get_workspace_path("__root__"));
        await db.open();
        return db;
    }

    async create_topology(topology_id) {
        let db = await this.open_workspace();
        await db.set_item(topology_id, {});
    }

    async open_topology(topology_id) {
        let db = await this.open_workspace();
        let item = await db.get_item(topology_id);
        if (item === null) {
            await db.set_item(topology_id, {});
        }
    }

    async remove_topology(topology_id) {
        let topology_db = new skadi.ClientStorage(this.get_topology_path(topology_id));
        await topology_db.remove();
        let db = await this.open_workspace();
        await db.remove_item(topology_id);
    }

    async list_topologies() {
        let db = await this.open_workspace();
        return await this.db.get_keys();
    }

    async get_topology_details() {
        let db = await this.open_workspace();
        let topology_ids = await db.get_keys();
        let topologies = {};
        for (let idx = 0; idx < topology_ids.length; idx++) {
            let topology_id = topology_ids[idx];
            let topology_db = new skadi.ClientStorage(this.get_topology_path(topology_id));
            let topology = await topology_db.get_item("topology.json");
            let obj = JSON.parse(topology);
            if (!obj) {
                obj = {"metadata": {}, "packages": []};
            }
            let metadata = obj["metadata"] || {};
            let packages = [];
            for (let node_id in obj["nodes"]) {
                let node_type = obj["nodes"][node_id]["node_type"];
                let package_id = node_type.split(":")[0];
                if (!packages.includes(package_id)) {
                    packages.push(package_id);
                }
            }
            topologies[topology_id] = {"metadata": metadata, "package_ids": packages};
        }
        return topologies;
    }

    async get_topology_detail(topology_id) {
        return await this.db.get_item(topology_id);
    }

    async load_topology(topology_id) {
        let db = new skadi.ClientStorage(this.get_topology_path(topology_id));
        let contents = await db.get_item("topology.json");

        if (contents) {
            return JSON.parse(contents);
        } else {
            return {};
        }
    }

    bind(designer_or_application) {
        this.designer_or_application = designer_or_application;

        // hook up to skadi events.  when the network changes, save them to local storage
        this.designer_or_application.add_node_event_handler("add", async (node_id, node_type_id) => {
            await this.save();
        });

        this.designer_or_application.add_node_event_handler("remove", async (node_id, node_type_id) => {
            await this.save();
        });

        this.designer_or_application.add_node_event_handler("update_position", async (node_id, node_type_id) => {
            await this.save();
        });

        this.designer_or_application.add_link_event_handler("add", async (link_id, link_type, from_node_id, from_port,
                                          to_node_id, to_port) => {
            await this.save();
        });

        this.designer_or_application.add_link_event_handler("remove", async (link_id, link_type, from_node_id, from_port,
                                             to_node_id, to_port) => {
            await this.save();
        });

        this.designer_or_application.add_design_event_handler("clear", async () => {
            await this.save();
        });

        this.designer_or_application.add_design_event_handler("update_design_metadata", async () => {
            await this.save();
        });

        this.designer_or_application.add_design_event_handler("update_metadata", async (node_id) => {
            await this.save();
        });
    }

    async save() {
        let saved = this.designer_or_application.save();
        let db = new skadi.ClientStorage(this.get_topology_path(this.designer_or_application.get_id()));
        await db.set_item("topology.json",JSON.stringify(saved, null, 4));
    }

    async get_save_link() {
        const zipFileWriter = new zip.BlobWriter();

        const zipWriter = new zip.ZipWriter(zipFileWriter);

        let db = new skadi.ClientStorage(this.get_topology_path(this.designer_or_application.get_id()));

        let data_keys = await db.get_keys();
        for(let j=0; j<data_keys.length; j++) {
            let data_key = data_keys[j];
            let data = await db.get_item(data_key);
            if (data instanceof String || typeof(data) == "string") {
                let rdr = new zip.TextReader(data);
                await zipWriter.add(data_key,rdr);
            } else if (data instanceof ArrayBuffer) {
                let rdr = new zip.BlobReader(new Blob([data]));
                await zipWriter.add(data_key,rdr);
            } else {
                // this should not happen
                console.error("Unable to serialise data " + data_key);
            }
        }

        await zipWriter.close();

        const zipFileBlob = await zipFileWriter.getData();
        let url = URL.createObjectURL(zipFileBlob);
        return url;
    }

    async load_from(file, node_renamings) {
        try {
            let db = new skadi.ClientStorage(this.get_topology_path(this.designer_or_application.get_id()));
            const fileReader = new zip.BlobReader(file);
            const zipReader = new zip.ZipReader(fileReader);
            let entries = await zipReader.getEntries();
            let topology_object = null;
            for (let idx = 0; idx < entries.length; idx++) {
                let entry = entries[idx];
                let name = entry.filename;
                let name_components = name.split("/");
                if (name_components[0] === "node") {
                    let node_id = name_components[1];
                    if (this.designer_or_application.get_network().has_node(node_id)) {
                        node_renamings[node_id] = this.designer_or_application.core.next_id("n");
                        name_components[1] = node_renamings[node_id];
                        name = name_components.join("/");
                    }
                }
                if (name === "topology.json" || name.endsWith("/properties.json")) {
                    let text = await entry.getData(new zip.TextWriter());
                    if (name === "topology.json") {
                        topology_object = JSON.parse(text);
                    }
                    await db.set_item(name, text);
                } else {
                    let blob = await entry.getData(new zip.BlobWriter());
                    let buffer = await blob.arrayBuffer();
                    await db.set_item(name, buffer);
                }
            }

            await this.designer_or_application.load(topology_object, node_renamings, []);
        } catch(ex) {
           console.error("load error: "+ex);
        }
    }

    get_file_suffix() {
        return ".zip";
    }

    async get_properties(topology_id, target_id, target_type) {
        let db = new skadi.ClientStorage(this.get_topology_path(topology_id));
        let path = target_type + "/" + target_id + "/properties.json";
        let properties = await db.get_item(path);
        properties = JSON.parse(properties);
        properties = properties || {};
        return properties;
    }

    async set_properties(topology_id, target_id, target_type, properties) {
        let db = new skadi.ClientStorage(this.get_topology_path(topology_id));
        let path = target_type + "/" + target_id + "/properties.json";
        await db.set_item(path, JSON.stringify(properties));
    }

    async get_data(topology_id, target_id, target_type, key) {
        skadi.ClientStorage.check_valid_key(key);
        let db = new skadi.ClientStorage(this.get_topology_path(topology_id));
        let path = target_type+"/"+target_id+"/data/"+key;
        return await db.get_item(path);
    }

    async set_data(topology_id, target_id, target_type, key, data) {
        skadi.ClientStorage.check_valid_key(key);
        skadi.ClientStorage.check_valid_value(data);
        let db = new skadi.ClientStorage(this.get_topology_path(topology_id));
        let path = target_type+"/"+target_id+"/data/"+key;
        if (data === null) {
            await db.remove_item(path);
        } else {
            await db.set_item(path, data);
        }
    }
}

/* src/js/common/x3.js */

var skadi = skadi || {};

skadi.X3Drag = class {

    constructor() {
        this.callbacks = {};
        this.elt = null;
        this.drag_overlay = document.getElementById("drag_overlay");
        this.start_time = null;
        this.start_pos = null;
    }

    set_element(elt) {
        this.elt = elt;
        let that = this;
        this.elt.onmousedown = function (evt) {
            that.start(evt);
        }
    }

    on(event, callback) {
        this.callbacks[event] = callback;
        return this;
    }

    start(evt) {
        let that = this;
        this.start_time = Date.now();
        evt.stopPropagation();

        document.onmousemove = function (evt) {
            that.initial_move(evt);
        }

        document.onmouseup = function (evt) {
            that.initial_mouseup(evt);
        }

        let pos = skadi.x3.get_event_xy(evt);
        this.start_pos = pos;
    }

    initial_move(evt) {
        document.onmousemove = null;
        document.onmouseup = null;
        this.enable_drag_overlay();
    }

    initial_mouseup(evt) {
        document.onmousemove = null;
        document.onmouseup = null;
    }

    enable_drag_overlay() {
        let that = this;
        this.drag_overlay.style = "pointer-events:all;";
        this.drag_overlay.setAttribute("width", "100%");
        this.drag_overlay.setAttribute("height", "100%");
        this.drag_overlay.setAttribute("class", "drag_overlay_open");

        this.drag_overlay.onmousemove = function (evt) {
            that.move(evt);
        }
        this.drag_overlay.onmouseup = function (evt) {
            that.end(evt);
        }
        this.drag_overlay_enabled = true;
    }

    start_cb() {
        if ("start" in this.callbacks) {
            this.callbacks["start"](this.start_pos.x, this.start_pos.y);
        }
        if ("start_abs" in this.callbacks) {
            this.callbacks["start_abs"](this.start_pos.ax, this.start_pos.ay);
        }
    }

    move(evt) {
        let elapsed_ms = Date.now() - this.start_time;
        if (elapsed_ms < 200) {
            return;
        }
        if (this.start_pos) {
            this.start_cb();
            this.start_pos = null;
        }
        evt.preventDefault();
        evt.stopPropagation();
        let pos = skadi.x3.get_event_xy(evt);
        if ("drag" in this.callbacks) {
            this.callbacks["drag"](pos.x, pos.y);
        }
        if ("drag_abs" in this.callbacks) {
            this.callbacks["drag_abs"](pos.ax, pos.ay);
        }
    }

    end(evt) {
        let elapsed_ms = Date.now() - this.start_time;
        if (elapsed_ms < 200) {
            // ignore drag end, this event should be followed by a click
        } else {
            if (this.start_pos) {
                this.start_cb();
                this.start_pos = null;
            }
            let pos = skadi.x3.get_event_xy(evt);
            evt.preventDefault();
            evt.stopPropagation();
            if ("end" in this.callbacks) {
                this.callbacks["end"](pos.x, pos.y);
            }
            if ("end_abs" in this.callbacks) {
                this.callbacks["end_abs"](pos.ax, pos.ay);
            }
        }
        this.drag_overlay.style = "pointer-events:none;";
        this.drag_overlay.setAttribute("width", 0);
        this.drag_overlay.setAttribute("height", 0);
        this.drag_overlay.setAttribute("class", "drag_overlay");
        this.drag_overlay.onmousemove = null;
        this.drag_overlay.onmouseup = null;
    }
}

skadi.X3Selection = class {

    constructor(elts) {
        this.svg_xmlns = "http://www.w3.org/2000/svg";
        this.xlink_xmlns = "http://www.w3.org/1999/xlink";
        this.html_xmlns = "http://www.w3.org/1999/xhtml";
        this.elts = [];

        for (let idx = 0; idx < elts.length; idx++) {
            this.elts.push(elts[idx]);
        }
    }

    add_node(node) {
        this.elts.push(node);
    }

    remove() {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].remove();
        }
        this.elts = [];
        return this;
    }

    is_svg(tag,parent_elt) {
        if (tag === "svg") {
            return true;
        }
        return parent_elt.namespaceURI === this.svg_xmlns;
    }

    append(tag, is_svg) {
        let newSelection = new skadi.X3Selection([]);
        for (let idx = 0; idx < this.elts.length; idx++) {
            let use_svg = is_svg;
            if (use_svg === undefined) {
                use_svg = this.is_svg(tag,this.elts[idx]);
            }
            let n;
            if (use_svg) {
                n = document.createElementNS(this.svg_xmlns, tag);
            } else {
                if (tag == "href") {
                    n = document.createElementNS(this.xlink_xmlns, tag);
                } else {
                    n = document.createElementNS(this.html_xmlns, tag);
                }
            }
            this.elts[idx].appendChild(n);
            newSelection.add_node(n);
        }
        return newSelection;
    }

    insert(tag, is_svg) {
        let newSelection = new skadi.X3Selection([]);
        for (let idx = 0; idx < this.elts.length; idx++) {
            let use_svg = is_svg;
            if (use_svg === undefined) {
                use_svg = this.is_svg(tag,this.elts[idx]);
            }
            let n;
            if (use_svg) {
                n = document.createElementNS(this.svg_xmlns, tag);
            } else {
                if (tag == "href") {
                    n = document.createElementNS(this.xlink_xmlns, tag);
                } else {
                    n = document.createElementNS(this.html_xmlns, tag);
                }
            }
            this.elts[idx].insertBefore(n, this.elts[idx].firstChild);
            newSelection.add_node(n);
        }
        return newSelection;
    }

    attr(name, value) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].setAttribute(name, value);
        }
        return this;
    }

    attr_ns(namespace, name, value) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].setAttributeNS(namespace, name, value);
        }
        return this;
    }

    text(txt) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            // this.elts[idx].innerHTML = "";
            let tn = document.createTextNode(txt);
            this.elts[idx].appendChild(tn);
        }
        return this;
    }

    add_class(cls) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].classList.add(cls);
        }
        return this;
    }

    remove_class(cls) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].classList.remove(cls);
        }
        return this;
    }

    html(inner_html) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].innerHTML = inner_html;
        }
        return this;
    }

    node() {
        if (this.elts) {
            return this.elts[0];
        } else {
            return null;
        }
    }

    style(name, value) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].style[name] = value;
        }
        return this;
    }

    call(drag) {
        if (this.elts.length === 1) {
            drag.set_element(this.elts[0]);
        } else {
            console.log("Unable to set up drag handler, not exactly 1 element in selection");
        }
    }

    on(event, callback) {
        for (let idx = 0; idx < this.elts.length; idx++) {
            this.elts[idx].addEventListener(event, callback);
        }
        return this;
    }

    ignore_mouse_events() {
        ["mousemove"].forEach(event_type =>
            this.on(event_type, evt => {
                evt.stopPropagation();
                evt.preventDefault();
            }));
    }
}

skadi.X3 = class {

    constructor() {
    }

    select(selector) {
        let elts = document.querySelectorAll(selector);
        return new skadi.X3Selection(elts);
    }

    get_node_pos(elt) {
        let body = this.select('body').node();

        let x = 0;
        let y = 0;

        while (elt != null && elt != body) {
            x += (elt.offsetLeft || elt.clientLeft);
            y += (elt.offsetTop || elt.clientTop);
            elt = (elt.offsetParent || elt.parentNode);
        }

        return {"x": x, "y": y};
    }

    get_event_xy(e) {
        let pos = {};
        pos.x = e.clientX;
        pos.y = e.clientY;
        pos.ax = pos.x + window.scrollX;
        pos.ay = pos.y + window.scrollY;
        return pos;
    }

}

skadi.x3 = new skadi.X3();
skadi.x3.drag = function () {
    return new skadi.X3Drag();
}

skadi.$ = (id) => {
    return document.getElementById(id);
}

skadi.$$ = (selector) => {
    return skadi.x3.select(selector);
}

skadi.$$$ = (id) => {
    return new skadi.X3Selection([skadi.$(id)]);
}




