/* src/js/schema/package_type.js */

var skadi = skadi || {};

skadi.PackageType = class {
  
  constructor(id, url, obj) {
    this.id = id;
    this.metadata = obj["metadata"];
    this.display = obj["display"];
    this.l10n = obj["l10n"];
    /* this.base_url = "";
    if (!url.startsWith("http")) {
      this.base_url = window.location.protocol+window.location.host;
    } */
    this.base_url = url.slice(0,url.lastIndexOf("/"));
    this.configuration = obj["configuration"];
    this.l10n_utils = null;
  }

  async load_l10n() {
    if (this.l10n) {
      this.l10n_utils = new skadi.L10NUtils("package."+this.id, this.base_url);
      this.l10n_utils.configure_for_package(this.l10n);
      await this.l10n_utils.initialise();
    }
  }

  get_l10n() {
    return this.l10n;
  }

  get_id() {
    return this.id;
  }

  get_name() {
    return this.localise(this.metadata["name"]);
  }

  get_description() {
    return this.localise(this.metadata["description"]);
  }

  get_metadata() {
    return this.metadata;
  }

  get_display() {
    return this.display;
  }

  get_base_url() {
    return this.base_url;
  }

  get_configuration_classname() {
    if (this.configuration && this.configuration.classname) {
      return this.configuration.classname;
    }
    return "";
  }

  get_url() {
    return this.metadata["url"];
  }

  get_resource_url(resource) {
    if (resource.startsWith("http")) {
      // resource is already an absolute URL
      return resource;
    }
    let resource_url =  this.base_url+"/"+resource;
    return String(resource_url);
  }

  get_qualified_id(id) {
    return this.id + ":" + id;
  }

  get_configuration() {
    return this.configuration;
  }

  get_l10n_utils() {
      return this.l10n_utils;
  }

  localise(txt) {
    if (this.l10n_utils) {
      return this.l10n_utils.localise(txt);
    } else {
      return txt;
    }
  }

  localise_url(url) {
    if (this.l10n_utils) {
      return url.replace("{language}",this.l10n_utils.get_language());
    } else {
      return url;
    }
  }
}



/* src/js/schema/port_type.js */

var skadi = skadi || {};

skadi.PortType = class {
  
  constructor(direction) {
    this.direction = direction;
    this.link_type = "";
    this.metadata = {};
    this.allow_multiple_connections = null;
  }

  deserialise(obj) {
    if (obj["link_type"]) {
      this.link_type = obj["link_type"];
    }
    if (obj["metadata"]) {
      this.metadata = obj["metadata"];
    }
    if ("allow_multiple_connections" in obj) {
      this.allow_multiple_connections = obj["allow_multiple_connections"];
    } else {
      this.allow_multiple_connections = true;
    }
  }

  serialise() {
    let result = {};
    result["channel_type"] = this.link_type;
    result["metadata"] = this.metadata;
    return result;
  }

  get_link_type() {
    return this.link_type;
  }

  get_allow_multiple_connections() {
    return this.allow_multiple_connections;
  }
}



/* src/js/schema/schema.js */

var skadi = skadi || {};

skadi.SchemaError = class extends Error {

    constructor(message) {
        super(message)
        this.name = "SkadiSchemaError";
    }

}

skadi.Schema = class {

    constructor() {
        this.package_types = {};
        this.node_types = {};
        this.link_types = {};
        this.resource_urls = [];
    }

    async load_package(url,obj,resource_loader) {
        let id = obj["id"];

        if (id in this.package_types) { // this package already loaded?
            return;
        }

        let package_type = new skadi.PackageType(id, url, obj);
        await package_type.load_l10n("");
        this.package_types[package_type.get_id()] = package_type;


        if (obj["resources"]) {
            obj["resources"].forEach(item => {
                this.resource_urls.push(package_type.get_resource_url(item))
            });
        }

        if (obj.node_types) {
            for(let nt_id in obj.node_types) {
                let nt = obj.node_types[nt_id];
                if (nt["resources"]) {
                    nt["resources"].forEach(item => {
                        if (!this.resource_urls.includes(url)) {
                            this.resource_urls.push(package_type.get_resource_url(item))
                        }
                    });
                }
            }
        }

        /* load resources defined in the package */

        if (resource_loader) {
            let load_results = await resource_loader.load_resources(this.resource_urls);
            let failed_resources = [];
            for (let resource_name in load_results) {
                if (!load_results[resource_name]) {
                    failed_resources.push(resource_name)
                }
            }

            /* raise an error if any resources failed to load */

            if (failed_resources.length > 0) {
                throw new skadi.SchemaError("Unable to load package resources: "+JSON.stringify(failed_resources))
            }
        }

        let node_types = obj["node_types"];
        for(let node_id in node_types) {
            let nt = node_types[node_id];
            let node_type = new skadi.NodeType(node_id, package_type, nt);
            if (node_type.is_enabled()) {
                this.node_types[node_type.get_id()] = node_type;
            }
        }

        let link_types = obj["link_types"];
        for(let link_id in link_types) {
            let lt = link_types[link_id];
            let link_type = new skadi.LinkType(link_id, package_type, lt);
            this.link_types[link_type.get_id()] = link_type;
        }

        return package_type;
    }

    get_resource_url(package_id, relative_path) {
       return this.package_types[package_id].get_resource_url(relative_path);
    }

    get_node_type(id) {
        return this.node_types[id];
    }

    get_class_map() {
        let class_map = { "nodes":{}, "configurations":{} };
        for(let node_type_id in this.node_types) {
            class_map["nodes"][node_type_id] = this.node_types[node_type_id].get_classname();
        }
        for(let package_id in this.package_types) {
            class_map["configurations"][package_id] = this.package_types[package_id].get_configuration_classname();
        }
        return class_map;
    }

    get_resource_urls() {
        return this.resource_urls;
    }

    get_link_type(id) {
        return this.link_types[id];
    }

    get_link_colour(id) {
        if (id in this.link_types) {
            return this.link_types[id].get_colour();
        }
        return "grey";
    }

    get_package_type(id) {
        return this.package_types[id];
    }

    has_package_type(id) {
        return (id in this.package_types);
    }

    get_package_types() {
        return Object.keys(this.package_types);
    }

    get_packages_list() {
        let result = [];
        for (let key in this.package_types) {
            let pt = this.package_types[key];
            result.push({"id":pt.get_id(),"metadata":pt.get_metadata()});
        }
        return result;
    }

    get_node_types() {
        let ids = [];
        for (let key in this.node_types) {
            ids.push(key);
        }
        return ids;
    }
}



/* src/js/skadi-api.js */

var skadi = skadi || {};

skadi.VERSION = "0.5.0";

skadi.api_home_url = document.currentScript.src.split("/").slice(0,-1).join("/");

skadi.Api = class {

    constructor() {
        this.core = null;
    }

    set_core(core) {
        this.core = core;
        window.addEventListener("beforeunload", (evt) => {
            // this is triggered only if the user has had some interaction with the page
            this.core.close();
            // evt.preventDefault();
        });
    }

    get_core() {
        return this.core;
    }

    has_node(node_id) {
        return this.core.get_network().has_node(node_id);
    }

    register_platform_extension(name, version, url) {
        this.extensions.push({
            "name": name,
            "version": version,
            "url": url
        });
    }

    async load_l10n(l10n_base_url) {
        let l10n_utils = new skadi.L10NUtils("system", l10n_base_url || skadi.api_home_url + "/l10n");
        await l10n_utils.initialise();
        return l10n_utils;
    }

    /**
     * Load schemas into skadi
     *
     * @param {Array} package_urls - an array of urls pointing to schema files
     * @param {Object} resource_loader - optional, a ResourceLoader class instance
     */
    async load_schema(package_urls, resource_loader) {
        async function load_package_from_url(url,schema) {
            return await fetch(url)
                .then(response => response.json())
                .then(schema_config => schema.load_package(url, schema_config, resource_loader));
        }

        let schema = new skadi.Schema();

        for (let idx = 0; idx < package_urls.length; idx++) {
            await load_package_from_url(package_urls[idx],schema);
        }
        return schema;
    }

    async load(from_obj, node_renamings, suppress_callbacks) {
        return await this.core.deserialise(from_obj ,node_renamings, suppress_callbacks);
    }

    save() {
        return this.core.serialise();
    }

    get_package_list() {
        return this.core.schema.get_package_types();
    }

    get_network() {
        return this.core.get_network();
    }

    set_node_status(node_id, status_msg, state) {
        this.core.update_node_status(node_id, state, status_msg);
    }

    set_configuration_status(package_id, status_msg, state) {
        this.core.update_configuration_status(package_id, state, status_msg);
    }

    update_execution_state(node_id, state, is_manual) {
        this.core.update_execution_state(node_id, state, is_manual);
    }

    execution_complete() {
        this.core.execution_complete();
    }

    get_version() {
        return skadi.version;
    }

    add_node_event_handler(node_event_type, handler) {
        switch(node_event_type) {
            case "add":
                this.core.add_node_event_handler(node_event_type,handler);
                break;
            case "remove":
                this.core.add_node_event_handler(node_event_type,handler);
                break;
            case "update_position":
                this.core.add_node_event_handler(node_event_type,handler);
                break;
            case "update_metadata":
                this.core.add_node_event_handler(node_event_type,handler);
                break;
            default:
                console.warn("Unhandled event type: "+node_event_type);

        }
        return handler;
    }

    remove_node_event_handler(node_event_type, handler) {
        this.core.remove_node_event_handler(node_event_type, handler);
    }

    add_link_event_handler(link_event_type, handler) {
        this.core.add_link_event_handler(link_event_type,handler);
        return handler;
    }

    remove_link_event_handler(link_event_type, handler) {
        this.core.remove_node_event_handler(link_event_type, handler);
    }

    add_design_event_handler(design_event_type, handler) {
        this.core.add_design_event_handler(design_event_type, handler);
        return handler;
    }

    remove_design_event_handler(design_event_type, handler) {
        this.core.remove_design_event_handler(design_event_type, handler);
    }
}

