/* src/js/skadi-directory-api.js */

var skadi = skadi || {};

skadi.DirectoryApi = class extends skadi.Api {

    constructor(element_id, options, plugins) {
        super();
        this.title = options.title || "";

        this.container = skadi.$$$(element_id);

        this.header = this.container.append("div").attr("class", "skadi_header");

        if (this.title) {
            this.header.append("h4").attr("class", "skadi_header_title").text(this.title);
        }

        this.root_element = this.container.append("div");

        this.root_list = {};
        this.open_topology_in_designer_callback = null;
        this.topologies = {};
        this.topology_metadata = {};
        this.topology_packages = {};
        this.options = options;
        this.plugins = plugins || {};
        this.l10n_utils = null;
        this.spinner = this.root_element.append("div").attr("class","exo-icon exo-icon-huge exo-vmargin exo-dark-purple exo-icon-spinner");
    }

    async load() {
        this.l10n_utils = await this.load_l10n(this.options.l10n_url);
        this.schema = await this.load_schema(this.options.schema_urls);
        this.topology_store = this.plugins.topology_store || new skadi.TopologyStore(this.options.workspace_id);
        await this.topology_store.init();
        let topology_details = await this.topology_store.get_topology_details();
        this.draw(topology_details);
        this.spinner.remove();
    }

    set_open_topology_in_designer_handler(callback) {
        this.open_topology_in_designer_callback = callback;
    }

    make_create_topology_callback() {
        return () => {
            let topology_id = this.create_name_input.node().value;

            if (!(topology_id in this.topology_metadata)) {
                this.topology_store.create_topology(topology_id);
            }
            this.add_topology(topology_id, {}, []);
        }
    }


    draw(initial_topologies) {
        let l10n_row = this.root_element.append("div").add_class("exo-row");
        let l10n_fieldset = l10n_row.append("p").append("fieldset");
        l10n_fieldset.append("legend").text(this.l10n_utils.localise("configuration.system.language"));
        l10n_fieldset.node().appendChild(this.l10n_utils.create_language_select());

        /* render controls */
        let control_row = this.root_element.append("div").add_class("exo-row");

        let create_fieldset = control_row.append("p").append("fieldset");
        create_fieldset.append("legend").text(this.l10n_utils.localise("create_topology"));
        let create_row = create_fieldset.append("div").add_class("exo-row");

        this.create_name_input = create_row.append("div").add_class("exo-2-cell")
            .text(this.l10n_utils.localise("topology_name"))
            .append("input").attr("type","text");

        this.create_name_input.on("input", (evt) => {
            if (evt.target.value === "") {
                this.update_creation_buttons(false);
            } else {
                this.update_creation_buttons(true);
            }
        });

        this.create_topology_button = create_row.append("div").add_class("exo-cell")
            .html("&#8203;") // blank label to align button with input box
            .append("input").attr("type","button").attr("value",this.l10n_utils.localise("create"));

        this.create_topology_button.on("click", this.make_create_topology_callback());

        /* render the tree with topologies */

        this.root_list = this.root_element.append("div").attr("class", "exo-tree").attr("role", "tree").append("ul");

        this.topologies = {};
        for(let topology_id in initial_topologies) {
            this.add_topology(topology_id, initial_topologies[topology_id]["metadata"],initial_topologies[topology_id]["package_ids"]);
        }
        this.update_creation_buttons(false);
    }

    add_topology(topology_id, metadata, package_ids) {

        if (topology_id in this.topologies) {
            return; // already exists?
        }
        this.topology_metadata[topology_id] = metadata;
        this.topology_packages[topology_id] = package_ids;

        this.render_topology(topology_id);
    }

    remove_topology(topology_id) {

        if (topology_id in this.topologies) {
            this.topologies[topology_id].treeitem.remove();
            delete this.topologies[topology_id];
        }
        if (topology_id in this.topology_metadata) {
            delete this.topology_metadata[topology_id];
            this.topology_store.remove_topology(topology_id);
        }
    }

    update_creation_buttons(enabled) {
        this.create_topology_button.node().disabled = !enabled;
    }

    render_topology(topology_id) {
        let metadata = this.topology_metadata[topology_id];
        let li = this.root_list.append("li").attr("role","treeitem");
        let input = li.append("input").attr("type","checkbox").attr("aria-hidden","true");
        let label = li.append("label");
        label.text(topology_id);

        label.append("a").attr("href","#0").text(this.l10n_utils.localise("open_topology_in_designer"))
            .add_class("open_link").on("click",() => {
                this.open_topology_in_designer_callback(topology_id);
        });

        label.insert("img").attr("src",skadi.icon_skadi).attr("class","treeicon");
        let pdiv = li.append("div");

        let div = pdiv.append("div").attr("class","exo-row");

        let pmetadata = {};
        for(let key in metadata) {
            pmetadata[key] = metadata[key];
        }

        let packages = this.topology_packages[topology_id];
        if (packages && packages.length>0) {
            pmetadata["packages"] =  packages.join(", ");
        }

        if (pmetadata) {
            let keys = [];
            for(let key in pmetadata) {
                if (pmetadata[key]) {
                    keys.push(key);
                }
            }
            if (keys.length>1) {
                let tbl = div.append("table").style("border","none");
                keys.forEach((key) => {
                    let lkey = this.l10n_utils.localise("metadata."+key);
                    let lcontent = pmetadata[key];
                    let txt = "";
                    lcontent.split("\n").forEach(item => {
                        if (txt != "") {
                            txt += "<br>"
                        }
                        txt += item;
                    });
                    let tr = tbl.append("tr").style("border","none");
                    tr.append("td").text(lkey + ":").style("border","none");
                    tr.append("td").html(txt).style("border","none");
                });
            }
        }

        div = pdiv.append("div").attr("class","exo-row");
        div.append("div").attr("exo-1-cell").append("input").attr("type","button").attr("value","Remove").on("click", (evt) => {
            this.remove_topology(topology_id);
        });

        this.topologies[topology_id] = { "treeitem":li };
    }
}

/**
  * Asynchronous function to create a skadi graphical editor widget
  *
  * @param {string} element_id - the name of the document element into which skadi will be loaded
  * @param {Object} options - various options to customise skadi
  * @param {Object} plugins - various plugins to customise skadi
  */
skadi.start_directory = async function(element_id, options, plugins) {

    let directory_api = new skadi.DirectoryApi(element_id, options, plugins);
    await directory_api.load();

    return directory_api;
}





